int newCorrelationTypeNotKnownId(CorrelationAttributeInstance.Type newType){
    Connection conn = connect();
    PreparedStatement preparedStatement = null;
    PreparedStatement preparedStatementQuery = null;
    ResultSet resultSet = null;
    int typeId = 0;
    String insertSql;
    String querySql;
    insertSql = "INSERT INTO correlation_types(display_name, db_table_name, supported, enabled) VALUES (?, ?, ?, ?) " + getConflictClause();
    querySql = "SELECT * FROM correlation_types WHERE display_name=? AND db_table_name=?";
    try {
        preparedStatement = conn.prepareStatement(insertSql);
        preparedStatement.setString(1, newType.getDisplayName());
        preparedStatement.setString(2, newType.getDbTableName());
        preparedStatement.setInt(3, newType.isSupported() ? 1 : 0);
        preparedStatement.setInt(4, newType.isEnabled() ? 1 : 0);
        preparedStatement.executeUpdate();
        preparedStatementQuery = conn.prepareStatement(querySql);
        preparedStatementQuery.setString(1, newType.getDisplayName());
        preparedStatementQuery.setString(2, newType.getDbTableName());
        resultSet = preparedStatementQuery.executeQuery();
        if (resultSet.next()) {
            CorrelationAttributeInstance.Type correlationType = getCorrelationTypeFromResultSet(resultSet);
            typeId = correlationType.getId();
        }
    } catch (SQLException ex) {
        throw new EamDbException("Error inserting new correlation type.", ex);
    } finally {
        EamDbUtil.closeStatement(preparedStatement);
        EamDbUtil.closeStatement(preparedStatementQuery);
        EamDbUtil.closeResultSet(resultSet);
        EamDbUtil.closeConnection(conn);
    }
    return typeId;
}

void showImageFx(AbstractFile file,Dimension dims){
    if (!fxInited) {
        return;
    }
    Platform.runLater(() -> {
        if (readImageTask != null) {
            readImageTask.cancel();
        }
        readImageTask = ImageUtils.newReadImageTask(file);
        readImageTask.setOnSucceeded(succeeded -> {
            if (!Case.isCaseOpen()) {
                reset();
                return;
            }
            try {
                Image fxImage = readImageTask.get();
                if (nonNull(fxImage)) {
                    fxImageView.setImage(fxImage);
                    borderpane.setCenter(fxImageView);
                } else {
                    showErrorNode(Bundle.MediaViewImagePanel_errorLabel_text(), file);
                }
            } catch (InterruptedException | ExecutionException ex) {
                showErrorNode(Bundle.MediaViewImagePanel_errorLabel_text(), file);
            }
            borderpane.setCursor(Cursor.DEFAULT);
        });
        readImageTask.setOnFailed(failed -> {
            if (!Case.isCaseOpen()) {
                reset();
                return;
            }
            Throwable exception = readImageTask.getException();
            if (exception instanceof OutOfMemoryError && exception.getMessage().contains("Java heap space")) {
                showErrorNode(Bundle.MediaViewImagePanel_errorLabel_OOMText(), file);
            } else {
                showErrorNode(Bundle.MediaViewImagePanel_errorLabel_text(), file);
            }
            borderpane.setCursor(Cursor.DEFAULT);
        });
        maskerPane.setProgressNode(progressBar);
        progressBar.progressProperty().bind(readImageTask.progressProperty());
        maskerPane.textProperty().bind(readImageTask.messageProperty());
        borderpane.setCenter(maskerPane);
        borderpane.setCursor(Cursor.WAIT);
        new Thread(readImageTask).start();
    });
}

