boolean isBitlockerVolume(Volume volume){
    boolean bitlockerVolume = false;
    byte[] bpbArray = new byte[BITLOCKER_BIOS_PARAMETER_BLOCK_SIZE];
    volume.read(bpbArray, 0, BITLOCKER_BIOS_PARAMETER_BLOCK_SIZE);
    boolean signatureMatches = true;
    for (int i = 0; i < BITLOCKER_SIGNATURE_BYTES.length; i++) {
        if (bpbArray[BITLOCKER_ADDRESS_SIGNATURE + i] != BITLOCKER_SIGNATURE_BYTES[i]) {
            signatureMatches = false;
            break;
        }
    }
    if (signatureMatches) {
        switch((int) bpbArray[BITLOCKER_ADDRESS_SECTORS_PER_CLUSTER]) {
            case 0x01:
            case 0x02:
            case 0x04:
            case 0x08:
            case 0x10:
            case 0x20:
            case 0x40:
            case 0x80:
                short reservedClusters = (short) ((bpbArray[BITLOCKER_ADDRESS_RESERVED_CLUSTERS] << 8) | (bpbArray[BITLOCKER_ADDRESS_RESERVED_CLUSTERS + 1] & 0xFF));
                byte fatCount = bpbArray[BITLOCKER_ADDRESS_FAT_COUNT];
                short rootEntries = (short) ((bpbArray[BITLOCKER_ADDRESS_ROOT_ENTRIES] << 8) | (bpbArray[BITLOCKER_ADDRESS_ROOT_ENTRIES + 1] & 0xFF));
                short sectors = (short) ((bpbArray[BITLOCKER_ADDRESS_SECTORS] << 8) | (bpbArray[BITLOCKER_ADDRESS_SECTORS + 1] & 0xFF));
                short sectorsPerFat = (short) ((bpbArray[BITLOCKER_ADDRESS_SECTORS_PER_FAT] << 8) | (bpbArray[BITLOCKER_ADDRESS_SECTORS_PER_FAT + 1] & 0xFF));
                int largeSectors = ((bpbArray[BITLOCKER_ADDRESS_LARGE_SECTORS] << 24) | ((bpbArray[BITLOCKER_ADDRESS_LARGE_SECTORS + 1] & 0xFF) << 16) | ((bpbArray[BITLOCKER_ADDRESS_LARGE_SECTORS + 2] & 0xFF) << 8) | (bpbArray[BITLOCKER_ADDRESS_LARGE_SECTORS + 3] & 0xFF));
                if (reservedClusters == 0 && fatCount == 0 && rootEntries == 0 && sectors == 0 && sectorsPerFat == 0 && largeSectors == 0) {
                    bitlockerVolume = true;
                }
                break;
            default:
        }
    }
    return bitlockerVolume;
}

Map<Integer, CommonAttributeValueList> getMetadata(Map<Integer, CommonAttributeValueList> getMetadata,int maximumPercentageThreshold){
    CorrelationAttributeInstance.Type attributeType = CorrelationAttributeInstance.getDefaultCorrelationTypes().stream().filter(filterType -> filterType.getId() == this.resultTypeId).findFirst().get();
    EamDb eamDb = EamDb.getInstance();
    Map<Integer, List<CommonAttributeValue>> itemsToRemove = new HashMap();
    Double uniqueCaseDataSourceTuples = eamDb.getCountUniqueDataSources().doubleValue();
    for (Entry<Integer, CommonAttributeValueList> listOfValues : Collections.unmodifiableMap(this.instanceCountToAttributeValues).entrySet()) {
        final Integer key = listOfValues.getKey();
        final CommonAttributeValueList values = listOfValues.getValue();
        for (CommonAttributeValue value : values.getDelayedMetadataList()) {
            boolean mimeTypeToRemove = false;
            if (!mimeTypesToInclude.isEmpty()) {
                for (AbstractCommonAttributeInstance commonAttr : value.getInstances()) {
                    AbstractFile abstractFile = commonAttr.getAbstractFile();
                    if (abstractFile != null) {
                        String mimeType = commonAttr.getAbstractFile().getMIMEType();
                        if (mimeType != null && !mimeTypesToInclude.contains(mimeType)) {
                            if (itemsToRemove.containsKey(key)) {
                                itemsToRemove.get(key).add(value);
                            } else {
                                List<CommonAttributeValue> toRemove = new ArrayList();
                                toRemove.add(value);
                                itemsToRemove.put(key, toRemove);
                            }
                            mimeTypeToRemove = true;
                            break;
                        }
                    }
                    if (mimeTypeToRemove) {
                        break;
                    }
                }
            }
            if (!mimeTypeToRemove && maximumPercentageThreshold != 0) {
                try {
                    Double uniqueTypeValueTuples = eamDb.getCountUniqueCaseDataSourceTuplesHavingTypeValue(attributeType, value.getValue()).doubleValue();
                    Double commonalityPercentage = uniqueTypeValueTuples / uniqueCaseDataSourceTuples * 100;
                    int frequencyPercentage = commonalityPercentage.intValue();
                    if (frequencyPercentage > maximumPercentageThreshold) {
                        if (itemsToRemove.containsKey(key)) {
                            itemsToRemove.get(key).add(value);
                        } else {
                            List<CommonAttributeValue> toRemove = new ArrayList();
                            toRemove.add(value);
                            itemsToRemove.put(key, toRemove);
                        }
                    }
                } catch (CorrelationAttributeNormalizationException ex) {
                    LOGGER.log(Level.WARNING, "Unable to determine frequency percentage attribute - frequency filter may not be accurate for these results.", ex);
                }
            }
        }
    }
    for (Entry<Integer, List<CommonAttributeValue>> valuesToRemove : itemsToRemove.entrySet()) {
        final Integer key = valuesToRemove.getKey();
        final List<CommonAttributeValue> values = valuesToRemove.getValue();
        for (CommonAttributeValue value : values) {
            final CommonAttributeValueList instanceCountValue = this.instanceCountToAttributeValues.get(key);
            if (instanceCountValue != null) {
                instanceCountValue.removeMetaData(value);
                if (instanceCountValue.getDelayedMetadataList().isEmpty()) {
                    this.instanceCountToAttributeValues.remove(key);
                }
            }
        }
    }
    return Collections.unmodifiableMap(this.instanceCountToAttributeValues);
}

