    } else if (dataType.startsWith(ARTIFACT_TYPE.TSK_ACCOUNT.getDisplayName())) {
        in = getClass().getResourceAsStream("/org/sleuthkit/autopsy/report/images/accounts.png");
        iconFileName = "accounts.png";
        iconFilePath = subPath + File.separator + iconFileName;
    } else {
        in = getClass().getResourceAsStream("/org/sleuthkit/autopsy/report/images/star.png");
        iconFileName = "star.png";
        iconFilePath = subPath + File.separator + iconFileName;
    }
    try {
        output = new FileOutputStream(iconFilePath);
        FileUtil.copy(in, output);
        in.close();
        output.close();
    } catch (IOException ex) {
        logger.log(Level.SEVERE, "Failed to extract images for HTML report.", ex);
    } finally {
        if (output != null) {
            try {
                output.flush();
                output.close();
            } catch (IOException ex) {
            }
        }
        if (in != null) {
            try {
                in.close();
            } catch (IOException ex) {
            }
        }
    }
    return iconFileName;
}

void dataSourceProcessorDone(UUID dataSourceId,DataSourceProcessorCallback.DataSourceProcessorResult result,List<String> errList,List<Content> contents){
    cleanupTask.disable();
    if (PlatformUtil.isWindowsOS() == true) {
        java.awt.Toolkit.getDefaultToolkit().beep();
    }
    AddImageWizardAddingProgressVisual panel = getComponent();
    if (panel != null) {
        Window w = SwingUtilities.getWindowAncestor(panel);
        if (w != null) {
            w.toFront();
        }
    }
    setStateFinished();
    if (result == DataSourceProcessorCallback.DataSourceProcessorResult.NO_ERRORS) {
        getComponent().setProgressBarTextAndColor(NbBundle.getMessage(this.getClass(), "AddImageWizardIngestConfigPanel.dsProcDone.noErrs.text"), 100, Color.black);
    } else {
        getComponent().setProgressBarTextAndColor(NbBundle.getMessage(this.getClass(), "AddImageWizardIngestConfigPanel.dsProcDone.errs.text"), 100, Color.red);
    }
    boolean critErr = false;
    if (result == DataSourceProcessorCallback.DataSourceProcessorResult.CRITICAL_ERRORS) {
        critErr = true;
    }
    for (String err : errList) {
        addErrors(err, critErr);
    }
    new Thread(() -> {
        try {
            if (!contents.isEmpty()) {
                Case.getCurrentCaseThrows().notifyDataSourceAdded(contents.get(0), dataSourceId);
            } else {
                Case.getCurrentCaseThrows().notifyFailedAddingDataSource(dataSourceId);
            }
        } catch (NoCurrentCaseException ex) {
            Logger.getLogger(AddImageWizardAddingProgressVisual.class.getName()).log(Level.SEVERE, "Exception while getting open case.", ex);
        }
    }).start();
    if (!cancelled) {
        newContents.clear();
        newContents.addAll(contents);
        setStateStarted();
        startIngest();
    } else {
        cancelled = false;
    }
}

