String longToDurationString(long duration){
    Duration d = Duration.ofMillis(duration);
    if (d.isNegative()) {
        d = Duration.ofMillis(-duration);
    }
    String result;
    long days = d.toDays();
    long hours = d.minusDays(days).toHours();
    long minutes = d.minusDays(days).minusHours(hours).toMinutes();
    long seconds = d.minusDays(days).minusHours(hours).minusMinutes(minutes).getSeconds();
    if (minutes > 0) {
        if (hours > 0) {
            if (days > 0) {
                result = days + " d  " + hours + " h  " + minutes + " m " + seconds + " s";
            } else {
                result = hours + " h  " + minutes + " m " + seconds + " s";
            }
        } else {
            result = minutes + " m " + seconds + " s";
        }
    } else {
        result = seconds + " s";
    }
    return result;
}

StaticLayout optimizeTextSize(TextLayoutParams params,int maxHeight,int maxLines,float maxFontSize,float minFontSize){
    final float threshold1 = 60.0f;
    final float threshold2 = 40.0f;
    final float extraStep1 = 3.0f;
    final float extraStep2 = 1.0f;
    boolean fits = false;
    StaticLayout textLayout = null;
    for (float fontSize = maxFontSize; fontSize >= minFontSize; fontSize -= 1.0f) {
        params.textPaint.setTextSize(fontSize);
        textLayout = buildLayout(params);
        if (textLayout.getHeight() <= maxHeight) {
            fits = true;
            break;
        }
        if (fontSize > threshold1) {
            fontSize -= extraStep1;
        } else if (fontSize > threshold2) {
            fontSize -= extraStep2;
        }
    }
    if (!fits) {
        final String textStr = params.text.toString();
        final int ellipsisLength = 3;
        final int ellipsisStart = textLayout != null ? textLayout.getLineStart(maxLines) - ellipsisLength : textStr.length();
        final int end = textStr.lastIndexOf(' ', ellipsisStart) + 1;
        if (end > 0) {
            textLayout = buildLayout(new TextLayoutParams(params, textStr.substring(0, end) + "..."));
            if (textLayout.getLineCount() <= maxLines) {
                fits = true;
            }
        }
    }
    if (!fits) {
        final float textRatio = .87f;
        final float maxWidth = textRatio * maxLines * params.lineWidth;
        textLayout = buildLayout(new TextLayoutParams(params, TextUtils.ellipsize(params.text, params.textPaint, maxWidth, TextUtils.TruncateAt.END)));
    }
    return textLayout;
}

