            builder.setPositiveButton(android.R.string.ok, (dialog, i) -> dialog.dismiss());
        } else {
            builder.setMessage(R.string.user_anon_blocked_from_editing);
            builder.setPositiveButton(R.string.nav_item_login, (dialog, i) -> {
                dialog.dismiss();
                Intent loginIntent = LoginActivity.newIntent(EditSectionActivity.this, LoginFunnel.SOURCE_BLOCKED);
                startActivity(loginIntent);
            });
            builder.setNegativeButton(android.R.string.cancel, (dialog, i) -> dialog.dismiss());
        }
        builder.show();
    } else if ("editconflict".equals(code)) {
        new AlertDialog.Builder(EditSectionActivity.this).setTitle(R.string.edit_conflict_title).setMessage(R.string.edit_conflict_message).setPositiveButton(android.R.string.ok, null).show();
        resetToStart();
    } else {
        showError(caught);
    }
}

