void bnDeleteCaseActionPerformed(java.awt.event.ActionEvent evt){
    if (completedTable.getModel().getRowCount() < 0 || completedTable.getSelectedRow() < 0) {
        return;
    }
    String caseName = (String) completedTable.getModel().getValueAt(completedTable.convertRowIndexToModel(completedTable.getSelectedRow()), JobsTableModelColumns.CASE.ordinal());
    Object[] options = { org.openide.util.NbBundle.getMessage(AutoIngestControlPanel.class, "ConfirmationDialog.Delete"), org.openide.util.NbBundle.getMessage(AutoIngestControlPanel.class, "ConfirmationDialog.DoNotDelete") };
    Object[] msgContent = { org.openide.util.NbBundle.getMessage(AutoIngestControlPanel.class, "ConfirmationDialog.DeleteAreYouSure") + "\"" + caseName + "\"?" };
    int reply = JOptionPane.showOptionDialog(this, msgContent, org.openide.util.NbBundle.getMessage(AutoIngestControlPanel.class, "ConfirmationDialog.ConfirmDeletionHeader"), JOptionPane.DEFAULT_OPTION, JOptionPane.WARNING_MESSAGE, null, options, options[JOptionPane.NO_OPTION]);
    if (reply == JOptionPane.YES_OPTION) {
        bnDeleteCase.setEnabled(false);
        bnShowCaseLog.setEnabled(false);
        if (completedTable.getModel().getRowCount() > 0 && completedTable.getSelectedRow() >= 0) {
            Path caseDirectoryPath = (Path) completedTable.getModel().getValueAt(completedTable.convertRowIndexToModel(completedTable.getSelectedRow()), JobsTableModelColumns.CASE_DIRECTORY_PATH.ordinal());
            completedTable.clearSelection();
            this.setCursor(Cursor.getPredefinedCursor(Cursor.WAIT_CURSOR));
            CaseDeletionResult result = manager.deleteCase(caseName, caseDirectoryPath);
            refreshTables();
            this.setCursor(Cursor.getPredefinedCursor(Cursor.DEFAULT_CURSOR));
            if (CaseDeletionResult.FAILED == result) {
                JOptionPane.showMessageDialog(this, String.format("Could not delete case %s. It may be in use.", caseName), org.openide.util.NbBundle.getMessage(AutoIngestControlPanel.class, "AutoIngestControlPanel.DeletionFailed"), JOptionPane.INFORMATION_MESSAGE);
            } else if (CaseDeletionResult.PARTIALLY_DELETED == result) {
                JOptionPane.showMessageDialog(this, String.format("Could not fully delete case %s. See system log for details.", caseName), org.openide.util.NbBundle.getMessage(AutoIngestControlPanel.class, "AutoIngestControlPanel.DeletionFailed"), JOptionPane.INFORMATION_MESSAGE);
            }
        }
    }
}

