void doCreateListsFromEntries(EncaseFileEntry entry,String parentPath){
    String name;
    if (parentPath.isEmpty()) {
        name = entry.name;
    } else {
        name = parentPath + "/" + entry.name;
    }
    List<Keyword> children = new ArrayList();
    for (EncaseFileEntry child : entry.children) {
        switch(child.type) {
            case Folder:
                doCreateListsFromEntries(child, name);
                break;
            case Expression:
                if (child.flags.contains(EncaseFlag.pg)) {
                    break;
                }
                children.add(new Keyword(child.value, true, true));
                break;
        }
    }
    if (theLists.containsKey(name)) {
        int i = 2;
        while (theLists.containsKey(name + "(" + i + ")")) {
            i += 1;
        }
        name = name + "(" + i + ")";
    }
    if (!children.isEmpty()) {
        KeywordList newList = new KeywordList(name, new Date(), new Date(), true, true, children);
        theLists.put(name, newList);
    }
}

