Signature getSignature(){
    FileType.Signature.Type sigType = signatureTypeComboBox.getSelectedItem() == RAW_SIGNATURE_TYPE_COMBO_BOX_ITEM ? FileType.Signature.Type.RAW : FileType.Signature.Type.ASCII;
    String sigString = signatureTextField.getText();
    if (sigString.isEmpty()) {
        JOptionPane.showMessageDialog(this, NbBundle.getMessage(FileTypeIdGlobalSettingsPanel.class, "FileTypeIdGlobalSettingsPanel.JOptionPane.invalidSignature.message"), NbBundle.getMessage(FileTypeIdGlobalSettingsPanel.class, "FileTypeIdGlobalSettingsPanel.JOptionPane.invalidSignature.title"), JOptionPane.ERROR_MESSAGE);
        return null;
    }
    byte[] signatureBytes;
    if (FileType.Signature.Type.RAW == sigType) {
        try {
            sigString = sigString.replaceAll("\\s", "");
            signatureBytes = DatatypeConverter.parseHexBinary(sigString);
        } catch (IllegalArgumentException ex) {
            JOptionPane.showMessageDialog(this, NbBundle.getMessage(FileTypeIdGlobalSettingsPanel.class, "FileTypeIdGlobalSettingsPanel.JOptionPane.invalidRawSignatureBytes.message"), NbBundle.getMessage(FileTypeIdGlobalSettingsPanel.class, "FileTypeIdGlobalSettingsPanel.JOptionPane.invalidSignatureBytes.title"), JOptionPane.ERROR_MESSAGE);
            return null;
        }
    } else {
        signatureBytes = sigString.getBytes(Charset.forName("UTF-8"));
    }
    long offset;
    boolean isRelativeToStart = offsetRelativeToComboBox.getSelectedItem() == START_OFFSET_RELATIVE_COMBO_BOX_ITEM;
    try {
        offset = Long.parseUnsignedLong(offsetTextField.getText());
        if (!isRelativeToStart && signatureBytes.length > offset + 1) {
            JOptionPane.showMessageDialog(this, NbBundle.getMessage(FileTypeIdGlobalSettingsPanel.class, "FileTypeIdGlobalSettingsPanel.JOptionPane.invalidOffset.length"), NbBundle.getMessage(FileTypeIdGlobalSettingsPanel.class, "FileTypeIdGlobalSettingsPanel.JOptionPane.invalidOffset.title"), JOptionPane.ERROR_MESSAGE);
            return null;
        }
    } catch (NumberFormatException ex) {
        JOptionPane.showMessageDialog(this, NbBundle.getMessage(FileTypeIdGlobalSettingsPanel.class, "FileTypeIdGlobalSettingsPanel.JOptionPane.invalidOffset.message"), NbBundle.getMessage(FileTypeIdGlobalSettingsPanel.class, "FileTypeIdGlobalSettingsPanel.JOptionPane.invalidOffset.title"), JOptionPane.ERROR_MESSAGE);
        return null;
    }
    FileType.Signature signature = new FileType.Signature(signatureBytes, offset, sigType, isRelativeToStart);
    return signature;
}

