Long getCountArtifactInstancesByCaseDataSource(CorrelationDataSource correlationDataSource){
    Connection conn = connect();
    Long instanceCount = 0L;
    List<CorrelationAttributeInstance.Type> artifactTypes = getDefinedCorrelationTypes();
    PreparedStatement preparedStatement = null;
    ResultSet resultSet = null;
    String sql = "SELECT 0 ";
    for (CorrelationAttributeInstance.Type type : artifactTypes) {
        String table_name = EamDbUtil.correlationTypeToInstanceTableName(type);
        sql += "+ (SELECT count(*) FROM " + table_name + " WHERE data_source_id=" + correlationDataSource.getID() + ")";
    }
    try {
        preparedStatement = conn.prepareStatement(sql);
        resultSet = preparedStatement.executeQuery();
        resultSet.next();
        instanceCount = resultSet.getLong(1);
    } catch (SQLException ex) {
        throw new EamDbException("Error counting artifact instances by caseName/dataSource.", ex);
    } finally {
        EamDbUtil.closeStatement(preparedStatement);
        EamDbUtil.closeResultSet(resultSet);
        EamDbUtil.closeConnection(conn);
    }
    return instanceCount;
}

void unpack(AbstractFile archiveFile,ConcurrentHashMap<Long, Archive> depthMap,boolean unpack,AbstractFile archiveFile,ConcurrentHashMap<Long, Archive> depthMap,String password){
    boolean unpackSuccessful = true;
    boolean hasEncrypted = false;
    boolean fullEncryption = true;
    boolean progressStarted = false;
    final String archiveFilePath = getArchiveFilePath(archiveFile);
    final String escapedArchiveFilePath = FileUtil.escapeFileName(archiveFilePath);
    HashMap<String, ZipFileStatusWrapper> statusMap = new HashMap();
    List<AbstractFile> unpackedFiles = Collections.<AbstractFile>emptyList();
    ISevenZipInArchive inArchive = null;
    currentArchiveName = archiveFile.getName();
    SevenZipContentReadStream stream = null;
    progress = ProgressHandle.createHandle(Bundle.EmbeddedFileExtractorIngestModule_ArchiveExtractor_moduleName());
    Archive parentAr;
    try {
        blackboard = Case.getCurrentCaseThrows().getServices().getBlackboard();
    } catch (NoCurrentCaseException ex) {
        logger.log(Level.INFO, "Exception while getting open case.", ex);
        unpackSuccessful = false;
        return unpackSuccessful;
    }
    try {
        List<AbstractFile> existingFiles = getAlreadyExtractedFiles(archiveFile, archiveFilePath);
        for (AbstractFile file : existingFiles) {
            statusMap.put(getKeyAbstractFile(file), new ZipFileStatusWrapper(file, ZipFileStatus.EXISTS));
        }
    } catch (TskCoreException e) {
        logger.log(Level.INFO, "Error checking if file already has been processed, skipping: {0}", escapedArchiveFilePath);
        unpackSuccessful = false;
        return unpackSuccessful;
    } catch (NoCurrentCaseException ex) {
        logger.log(Level.INFO, "No open case was found while trying to unpack the archive file {0}", escapedArchiveFilePath);
        unpackSuccessful = false;
        return unpackSuccessful;
    }
    parentAr = depthMap.get(archiveFile.getId());
    if (parentAr == null) {
        parentAr = new Archive(0, archiveFile.getId(), archiveFile);
        depthMap.put(archiveFile.getId(), parentAr);
    } else {
        Archive rootArchive = depthMap.get(parentAr.getRootArchiveId());
        if (rootArchive.isFlaggedAsZipBomb()) {
            unpackSuccessful = false;
            return unpackSuccessful;
        } else if (parentAr.getDepth() == MAX_DEPTH) {
            String details = NbBundle.getMessage(SevenZipExtractor.class, "EmbeddedFileExtractorIngestModule.ArchiveExtractor.unpack.warnDetails.zipBomb", parentAr.getDepth(), FileUtil.escapeFileName(getArchiveFilePath(rootArchive.getArchiveFile())));
            flagRootArchiveAsZipBomb(rootArchive, archiveFile, details, escapedArchiveFilePath);
            unpackSuccessful = false;
            return unpackSuccessful;
        }
    }
    try {
        stream = new SevenZipContentReadStream(new ReadContentInputStream(archiveFile));
        ArchiveFormat options = get7ZipOptions(archiveFile);
        if (password == null) {
            inArchive = SevenZip.openInArchive(options, stream);
        } else {
            inArchive = SevenZip.openInArchive(options, stream, password);
        }
        numItems = inArchive.getNumberOfItems();
        progress.start(numItems);
        progressStarted = true;
        final String uniqueArchiveFileName = FileUtil.escapeFileName(EmbeddedFileExtractorIngestModule.getUniqueName(archiveFile));
        try {
            makeLocalDirectories(uniqueArchiveFileName);
        } catch (SecurityException e) {
            logger.log(Level.SEVERE, "Error setting up output path for archive root: {0}", getLocalRootAbsPath(uniqueArchiveFileName));
            unpackSuccessful = false;
            return unpackSuccessful;
        }
        SevenZipExtractor.UnpackedTree unpackedTree = new SevenZipExtractor.UnpackedTree(moduleDirRelative + "/" + uniqueArchiveFileName, archiveFile);
        long freeDiskSpace;
        try {
            freeDiskSpace = services.getFreeDiskSpace();
        } catch (NullPointerException ex) {
            freeDiskSpace = IngestMonitor.DISK_FREE_SPACE_UNKNOWN;
        }
        Map<Integer, InArchiveItemDetails> archiveDetailsMap = new HashMap();
        for (int inArchiveItemIndex = 0; inArchiveItemIndex < numItems; inArchiveItemIndex++) {
            progress.progress(String.format("%s: Analyzing archive metadata and creating local files (%d of %d)", currentArchiveName, inArchiveItemIndex + 1, numItems), 0);
            if (isZipBombArchiveItemCheck(archiveFile, inArchive, inArchiveItemIndex, depthMap, escapedArchiveFilePath)) {
                unpackSuccessful = false;
                return unpackSuccessful;
            }
            String pathInArchive = getPathInArchive(inArchive, inArchiveItemIndex, archiveFile);
            UnpackedTree.UnpackedNode unpackedNode = unpackedTree.addNode(pathInArchive);
            final boolean isEncrypted = (Boolean) inArchive.getProperty(inArchiveItemIndex, PropID.ENCRYPTED);
            if (isEncrypted && password == null) {
                logger.log(Level.WARNING, "Skipping encrypted file in archive: {0}", pathInArchive);
                hasEncrypted = true;
                unpackSuccessful = false;
                continue;
            } else {
                fullEncryption = false;
            }
            Long archiveItemSize = (Long) inArchive.getProperty(inArchiveItemIndex, PropID.SIZE);
            if (freeDiskSpace != IngestMonitor.DISK_FREE_SPACE_UNKNOWN && archiveItemSize != null && archiveItemSize > 0) {
                String archiveItemPath = (String) inArchive.getProperty(inArchiveItemIndex, PropID.PATH);
                long newDiskSpace = freeDiskSpace - archiveItemSize;
                if (newDiskSpace < MIN_FREE_DISK_SPACE) {
                    String msg = NbBundle.getMessage(SevenZipExtractor.class, "EmbeddedFileExtractorIngestModule.ArchiveExtractor.unpack.notEnoughDiskSpace.msg", escapedArchiveFilePath, archiveItemPath);
                    String details = NbBundle.getMessage(SevenZipExtractor.class, "EmbeddedFileExtractorIngestModule.ArchiveExtractor.unpack.notEnoughDiskSpace.details");
                    services.postMessage(IngestMessage.createErrorMessage(EmbeddedFileExtractorModuleFactory.getModuleName(), msg, details));
                    logger.log(Level.INFO, "Skipping archive item due to insufficient disk space: {0}, {1}", new String[] { escapedArchiveFilePath, archiveItemPath });
                    logger.log(Level.INFO, "Available disk space: {0}", new Object[] { freeDiskSpace });
                    unpackSuccessful = false;
                    continue;
                } else {
                    freeDiskSpace = newDiskSpace;
                }
            }
            final String uniqueExtractedName = FileUtil.escapeFileName(uniqueArchiveFileName + File.separator + (inArchiveItemIndex / 1000) + File.separator + inArchiveItemIndex + "_" + new File(pathInArchive).getName());
            final String localAbsPath = moduleDirAbsolute + File.separator + uniqueExtractedName;
            final String localRelPath = moduleDirRelative + File.separator + uniqueExtractedName;
            File localFile = new java.io.File(localAbsPath);
            if (!localFile.exists()) {
                try {
                    if ((Boolean) inArchive.getProperty(inArchiveItemIndex, PropID.IS_FOLDER)) {
                        localFile.mkdirs();
                    } else {
                        localFile.getParentFile().mkdirs();
                        try {
                            localFile.createNewFile();
                        } catch (IOException e) {
                            logger.log(Level.SEVERE, "Error creating extracted file: " + localFile.getAbsolutePath(), e);
                        }
                    }
                } catch (SecurityException e) {
                    logger.log(Level.SEVERE, "Error setting up output path for unpacked file: {0}", pathInArchive);
                }
            }
            if (localFile.exists() == false) {
                continue;
            }
            archiveDetailsMap.put(inArchiveItemIndex, new InArchiveItemDetails(unpackedNode, localAbsPath, localRelPath));
        }
        int[] extractionIndices = getExtractableFilesFromDetailsMap(archiveDetailsMap);
        StandardIArchiveExtractCallback archiveCallBack = new StandardIArchiveExtractCallback(inArchive, archiveFile, progress, archiveDetailsMap, password, freeDiskSpace);
        inArchive.extract(extractionIndices, false, archiveCallBack);
        unpackSuccessful = unpackSuccessful & archiveCallBack.wasSuccessful();
        archiveDetailsMap = null;
        try {
            unpackedTree.updateOrAddFileToCaseRec(statusMap, archiveFilePath);
            unpackedFiles = unpackedTree.getAllFileObjects();
            for (int i = 0; i < unpackedFiles.size(); i++) {
                progress.progress(String.format("%s: Searching for nested archives (%d of %d)", currentArchiveName, i + 1, unpackedFiles.size()));
                AbstractFile unpackedFile = unpackedFiles.get(i);
                if (unpackedFile == null) {
                    continue;
                }
                if (isSevenZipExtractionSupported(unpackedFile)) {
                    Archive child = new Archive(parentAr.getDepth() + 1, parentAr.getRootArchiveId(), archiveFile);
                    parentAr.addChild(child);
                    depthMap.put(unpackedFile.getId(), child);
                }
            }
        } catch (TskCoreException | NoCurrentCaseException e) {
            logger.log(Level.SEVERE, "Error populating complete derived file hierarchy from the unpacked dir structure", e);
        }
    } catch (SevenZipException ex) {
        logger.log(Level.WARNING, "Error unpacking file: " + archiveFile, ex);
        if (archiveFile.isMetaFlagSet(TskData.TSK_FS_META_FLAG_ENUM.ALLOC)) {
            String msg = NbBundle.getMessage(SevenZipExtractor.class, "EmbeddedFileExtractorIngestModule.ArchiveExtractor.unpack.errUnpacking.msg", currentArchiveName);
            String details = NbBundle.getMessage(SevenZipExtractor.class, "EmbeddedFileExtractorIngestModule.ArchiveExtractor.unpack.errUnpacking.details", escapedArchiveFilePath, ex.getMessage());
            services.postMessage(IngestMessage.createErrorMessage(EmbeddedFileExtractorModuleFactory.getModuleName(), msg, details));
        }
    } finally {
        if (inArchive != null) {
            try {
                inArchive.close();
            } catch (SevenZipException e) {
                logger.log(Level.SEVERE, "Error closing archive: " + archiveFile, e);
            }
        }
        if (stream != null) {
            try {
                stream.close();
            } catch (IOException ex) {
                logger.log(Level.SEVERE, "Error closing stream after unpacking archive: " + archiveFile, ex);
            }
        }
        if (progressStarted) {
            progress.finish();
        }
    }
    if (hasEncrypted) {
        String encryptionType = fullEncryption ? ENCRYPTION_FULL : ENCRYPTION_FILE_LEVEL;
        try {
            BlackboardArtifact artifact = archiveFile.newArtifact(BlackboardArtifact.ARTIFACT_TYPE.TSK_ENCRYPTION_DETECTED);
            artifact.addAttribute(new BlackboardAttribute(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_COMMENT, EmbeddedFileExtractorModuleFactory.getModuleName(), encryptionType));
            try {
                blackboard.indexArtifact(artifact);
            } catch (Blackboard.BlackboardException ex) {
                logger.log(Level.SEVERE, "Unable to index blackboard artifact " + artifact.getArtifactID(), ex);
                MessageNotifyUtil.Notify.error(Bundle.SevenZipExtractor_indexError_message(), artifact.getDisplayName());
            }
            services.fireModuleDataEvent(new ModuleDataEvent(EmbeddedFileExtractorModuleFactory.getModuleName(), BlackboardArtifact.ARTIFACT_TYPE.TSK_ENCRYPTION_DETECTED));
        } catch (TskCoreException ex) {
            logger.log(Level.SEVERE, "Error creating blackboard artifact for encryption detected for file: " + escapedArchiveFilePath, ex);
        }
        String msg = NbBundle.getMessage(SevenZipExtractor.class, "EmbeddedFileExtractorIngestModule.ArchiveExtractor.unpack.encrFileDetected.msg");
        String details = NbBundle.getMessage(SevenZipExtractor.class, "EmbeddedFileExtractorIngestModule.ArchiveExtractor.unpack.encrFileDetected.details", currentArchiveName, EmbeddedFileExtractorModuleFactory.getModuleName());
        services.postMessage(IngestMessage.createWarningMessage(EmbeddedFileExtractorModuleFactory.getModuleName(), msg, details));
    }
    if (!unpackedFiles.isEmpty()) {
        services.fireModuleContentEvent(new ModuleContentEvent(archiveFile));
        if (context != null) {
            context.addFilesToJob(unpackedFiles);
        }
    }
    return unpackSuccessful;
}

