void refreshTable(List<AutoIngestJob> jobs,DefaultTableModel tableModel,Comparator<AutoIngestJob> comparator){
    try {
        if (comparator != null) {
            jobs.sort(comparator);
        }
        tableModel.setRowCount(0);
        for (AutoIngestJob job : jobs) {
            AutoIngestJob.StageDetails status = job.getProcessingStageDetails();
            tableModel.addRow(new Object[] { job.getManifest().getCaseName(), job.getManifest().getDataSourcePath().getFileName(), job.getProcessingHostName(), job.getManifest().getDateFileCreated(), job.getProcessingStageStartDate(), job.getCompletedDate(), status.getDescription(), job.getErrorsOccurred() ? StatusIconCellRenderer.Status.WARNING : StatusIconCellRenderer.Status.OK, ((Date.from(Instant.now()).getTime()) - (status.getStartDate().getTime())), job.getCaseDirectoryPath(), job.getProcessingHostName().equals(LOCAL_HOST_NAME), job.getManifest().getFilePath(), job.getPriority() });
