boolean contentHasVisibleContentChildren(Content c){
    if (c != null) {
        try {
            if (!c.hasChildren()) {
                return false;
            }
        } catch (TskCoreException ex) {
            logger.log(Level.SEVERE, "Error checking if the node has children, for content: " + c, ex);
            return false;
        }
        String query = "SELECT COUNT(obj_id) AS count FROM " + " ( SELECT obj_id FROM tsk_objects WHERE par_obj_id = " + c.getId() + " AND type = " + TskData.ObjectType.ARTIFACT.getObjectType() + "   INTERSECT SELECT artifact_obj_id FROM blackboard_artifacts WHERE obj_id = " + c.getId() + "     AND (artifact_type_id = " + ARTIFACT_TYPE.TSK_EMAIL_MSG.getTypeID() + " OR artifact_type_id = " + ARTIFACT_TYPE.TSK_MESSAGE.getTypeID() + ") " + "   UNION SELECT obj_id FROM tsk_objects WHERE par_obj_id = " + c.getId() + "     AND type = " + TskData.ObjectType.ABSTRACTFILE.getObjectType() + ") AS OBJECT_IDS";
        try (SleuthkitCase.CaseDbQuery dbQuery = Case.getCurrentCaseThrows().getSleuthkitCase().executeQuery(query)) {
            ResultSet resultSet = dbQuery.getResultSet();
            if (resultSet.next()) {
                return (0 < resultSet.getInt("count"));
            }
        } catch (TskCoreException | SQLException | NoCurrentCaseException ex) {
            logger.log(Level.SEVERE, "Error checking if the node has children, for content: " + c, ex);
        }
    }
    return false;
}

