ParentPathCondition readPathCondition(Element ruleElement){
    ParentPathCondition pathCondition = null;
    if (!ruleElement.getAttribute(PATH_FILTER_ATTR).isEmpty() || !ruleElement.getAttribute(PATH_REGEX_ATTR).isEmpty()) {
        String path = ruleElement.getAttribute(PATH_FILTER_ATTR);
        String pathRegex = ruleElement.getAttribute(PATH_REGEX_ATTR);
        if (!pathRegex.isEmpty() && path.isEmpty()) {
            try {
                Pattern pattern = Pattern.compile(pathRegex);
                pathCondition = new ParentPathCondition(pattern);
            } catch (PatternSyntaxException ex) {
                logger.log(Level.SEVERE, "Error compiling " + PATH_REGEX_ATTR + " regex, ignoring malformed path condition definition", ex);
                throw new FilesSetsManager.FilesSetsManagerException(String.format("error compiling %s regex", PATH_REGEX_ATTR), ex);
            }
        } else if (!path.isEmpty() && pathRegex.isEmpty()) {
            pathCondition = new ParentPathCondition(path);
        }
        if (pathCondition == null) {
            throw new FilesSetsManager.FilesSetsManagerException(String.format("Error creating path condition for rule %s", readRuleName(ruleElement)));
        }
    }
    return pathCondition;
}

BufferedImage getThumbnail(Content content,int iconSize){
    if (content instanceof AbstractFile) {
        AbstractFile file = (AbstractFile) content;
        if (ImageUtils.isGIF(file)) {
            try (BufferedInputStream bufferedReadContentStream = getBufferedReadContentStream(file)) {
                if (Thread.interrupted()) {
                    return DEFAULT_THUMBNAIL;
                }
                final BufferedImage image = ImageIO.read(bufferedReadContentStream);
                if (image != null) {
                    if (Thread.interrupted()) {
                        return DEFAULT_THUMBNAIL;
                    }
                    return ScalrWrapper.resizeHighQuality(image, iconSize, iconSize);
                }
            } catch (IOException iOException) {
                LOGGER.log(Level.WARNING, "Failed to get thumbnail for " + getContentPathSafe(content), iOException);
            }
            return DEFAULT_THUMBNAIL;
        }
        Task<javafx.scene.image.Image> thumbnailTask = newGetThumbnailTask(file, iconSize, true);
        if (Thread.interrupted()) {
            return DEFAULT_THUMBNAIL;
        }
        thumbnailTask.run();
        try {
            return SwingFXUtils.fromFXImage(thumbnailTask.get(), null);
        } catch (InterruptedException | ExecutionException ex) {
            LOGGER.log(Level.WARNING, "Failed to get thumbnail for " + getContentPathSafe(content), ex);
        }
    }
    return DEFAULT_THUMBNAIL;
}

