void getHistory(){
    logger.log(Level.INFO, "Pasco results path: {0}", moduleTempResultsDir);
    boolean foundHistory = false;
    final File pascoRoot = InstalledFileLocator.getDefault().locate("pasco2", ExtractIE.class.getPackage().getName(), false);
    if (pascoRoot == null) {
        this.addErrorMessage(NbBundle.getMessage(this.getClass(), "ExtractIE.getHistory.errMsg.unableToGetHist", this.getName()));
        logger.log(Level.SEVERE, "Error finding pasco program ");
        return;
    }
    final String pascoHome = pascoRoot.getAbsolutePath();
    logger.log(Level.INFO, "Pasco2 home: {0}", pascoHome);
    PASCO_LIB_PATH = pascoHome + File.separator + "pasco2.jar" + File.pathSeparator + pascoHome + File.separator + "*";
    File resultsDir = new File(moduleTempResultsDir);
    resultsDir.mkdirs();
    org.sleuthkit.autopsy.casemodule.services.FileManager fileManager = currentCase.getServices().getFileManager();
    List<AbstractFile> indexFiles;
    try {
        indexFiles = fileManager.findFiles(dataSource, "index.dat");
    } catch (TskCoreException ex) {
        this.addErrorMessage(NbBundle.getMessage(this.getClass(), "ExtractIE.getHistory.errMsg.errGettingHistFiles", this.getName()));
        logger.log(Level.WARNING, "Error fetching 'index.data' files for Internet Explorer history.");
        return;
    }
    if (indexFiles.isEmpty()) {
        String msg = NbBundle.getMessage(this.getClass(), "ExtractIE.getHistory.errMsg.noHistFiles");
        logger.log(Level.INFO, msg);
        return;
    }
    dataFound = true;
    Collection<BlackboardArtifact> bbartifacts = new ArrayList();
    String temps;
    String indexFileName;
    for (AbstractFile indexFile : indexFiles) {
        indexFileName = "index" + Integer.toString((int) indexFile.getId()) + ".dat";
        temps = RAImageIngestModule.getRATempPath(currentCase, "IE") + File.separator + indexFileName;
        File datFile = new File(temps);
        if (context.dataSourceIngestIsCancelled()) {
            break;
        }
        try {
            ContentUtils.writeToFile(indexFile, datFile, context::dataSourceIngestIsCancelled);
        } catch (IOException e) {
            logger.log(Level.WARNING, "Error while trying to write index.dat file " + datFile.getAbsolutePath(), e);
            this.addErrorMessage(NbBundle.getMessage(this.getClass(), "ExtractIE.getHistory.errMsg.errWriteFile", this.getName(), datFile.getAbsolutePath()));
            continue;
        }
        String filename = "pasco2Result." + indexFile.getId() + ".txt";
        boolean bPascProcSuccess = executePasco(temps, filename);
        if (context.dataSourceIngestIsCancelled()) {
            return;
        }
        if (bPascProcSuccess) {
            bbartifacts.addAll(parsePascoOutput(indexFile, filename).stream().filter(bbart -> bbart.getArtifactTypeID() == ARTIFACT_TYPE.TSK_WEB_HISTORY.getTypeID()).collect(Collectors.toList()));
            foundHistory = true;
            datFile.delete();
        } else {
            logger.log(Level.WARNING, "pasco execution failed on: {0}", this.getName());
            this.addErrorMessage(NbBundle.getMessage(this.getClass(), "ExtractIE.getHistory.errMsg.errProcHist", this.getName()));
        }
    }
    if (foundHistory) {
        services.fireModuleDataEvent(new ModuleDataEvent(NbBundle.getMessage(this.getClass(), "ExtractIE.parentModuleName"), BlackboardArtifact.ARTIFACT_TYPE.TSK_WEB_HISTORY, bbartifacts));
    }
}

