void writeHashsetHits(TableReportModule tableModule,String comment,HashSet<String> tagNamesFilter){
    String orderByClause;
    Case openCase;
    try {
        openCase = Case.getCurrentCaseThrows();
    } catch (NoCurrentCaseException ex) {
        errorList.add(Bundle.ReportGenerator_errList_noOpenCase());
        logger.log(Level.SEVERE, "Exception while getting open case: ", ex);
        return;
    }
    if (openCase.getCaseType() == Case.CaseType.MULTI_USER_CASE) {
        orderByClause = "ORDER BY convert_to(att.value_text, 'SQL_ASCII') ASC NULLS FIRST";
    } else {
        orderByClause = "ORDER BY att.value_text ASC";
    }
    String hashsetsQuery = "SELECT att.value_text AS list " + "FROM blackboard_attributes AS att, blackboard_artifacts AS art " + "WHERE att.attribute_type_id = " + BlackboardAttribute.ATTRIBUTE_TYPE.TSK_SET_NAME.getTypeID() + " " + "AND art.artifact_type_id = " + BlackboardArtifact.ARTIFACT_TYPE.TSK_HASHSET_HIT.getTypeID() + " " + "AND att.artifact_id = art.artifact_id " + "GROUP BY list " + orderByClause;
    try (SleuthkitCase.CaseDbQuery dbQuery = openCase.getSleuthkitCase().executeQuery(hashsetsQuery)) {
        ResultSet listsRs = dbQuery.getResultSet();
        List<String> lists = new ArrayList();
        while (listsRs.next()) {
            lists.add(listsRs.getString("list"));
        }
        tableModule.startDataType(BlackboardArtifact.ARTIFACT_TYPE.TSK_HASHSET_HIT.getDisplayName(), comment);
        tableModule.addSetIndex(lists);
        progressPanel.updateStatusLabel(NbBundle.getMessage(this.getClass(), "ReportGenerator.progress.processing", BlackboardArtifact.ARTIFACT_TYPE.TSK_HASHSET_HIT.getDisplayName()));
    } catch (TskCoreException | SQLException ex) {
        errorList.add(NbBundle.getMessage(this.getClass(), "ReportGenerator.errList.failedQueryHashsetLists"));
        logger.log(Level.SEVERE, "Failed to query hashset lists: ", ex);
        return;
    }
    if (openCase.getCaseType() == Case.CaseType.MULTI_USER_CASE) {
        orderByClause = "ORDER BY convert_to(att.value_text, 'SQL_ASCII') ASC NULLS FIRST, " + "convert_to(f.parent_path, 'SQL_ASCII') ASC NULLS FIRST, " + "convert_to(f.name, 'SQL_ASCII') ASC NULLS FIRST, " + "size ASC NULLS FIRST";
    } else {
        orderByClause = "ORDER BY att.value_text ASC, f.parent_path ASC, f.name ASC, size ASC";
    }
    String hashsetHitsQuery = "SELECT art.artifact_id, art.obj_id, att.value_text AS setname, f.name AS name, f.size AS size, f.parent_path AS parent_path " + "FROM blackboard_artifacts AS art, blackboard_attributes AS att, tsk_files AS f " + "WHERE (att.artifact_id = art.artifact_id) " + "AND (f.obj_id = art.obj_id) " + "AND (att.attribute_type_id = " + BlackboardAttribute.ATTRIBUTE_TYPE.TSK_SET_NAME.getTypeID() + ") " + "AND (art.artifact_type_id = " + BlackboardArtifact.ARTIFACT_TYPE.TSK_HASHSET_HIT.getTypeID() + ") " + orderByClause;
    try (SleuthkitCase.CaseDbQuery dbQuery = openCase.getSleuthkitCase().executeQuery(hashsetHitsQuery)) {
        ResultSet resultSet = dbQuery.getResultSet();
        String currentSet = "";
        while (resultSet.next()) {
            if (progressPanel.getStatus() == ReportProgressPanel.ReportStatus.CANCELED) {
                break;
            }
            HashSet<String> uniqueTagNames = getUniqueTagNames(resultSet.getLong("artifact_id"));
            if (failsTagFilter(uniqueTagNames, tagNamesFilter)) {
                continue;
            }
            String tagsList = makeCommaSeparatedList(uniqueTagNames);
            Long objId = resultSet.getLong("obj_id");
            String set = resultSet.getString("setname");
            String size = resultSet.getString("size");
            String uniquePath = "";
            try {
                AbstractFile f = openCase.getSleuthkitCase().getAbstractFileById(objId);
                if (f != null) {
                    uniquePath = openCase.getSleuthkitCase().getAbstractFileById(objId).getUniquePath();
                }
            } catch (TskCoreException ex) {
                errorList.add(NbBundle.getMessage(this.getClass(), "ReportGenerator.errList.failedGetAbstractFileFromID"));
                logger.log(Level.WARNING, "Failed to get Abstract File from ID.", ex);
                return;
            }
            if (!set.equals(currentSet)) {
                if (!currentSet.isEmpty()) {
                    tableModule.endTable();
                    tableModule.endSet();
                }
                currentSet = set;
                tableModule.startSet(currentSet);
                List<String> columnHeaderNames = new ArrayList();
                columnHeaderNames.add(NbBundle.getMessage(this.getClass(), "ReportGenerator.artTableColHdr.file"));
                columnHeaderNames.add(NbBundle.getMessage(this.getClass(), "ReportGenerator.artTableColHdr.size"));
                columnHeaderNames.add(NbBundle.getMessage(this.getClass(), "ReportGenerator.artTableColHdr.tags"));
                tableModule.startTable(columnHeaderNames);
                progressPanel.updateStatusLabel(NbBundle.getMessage(this.getClass(), "ReportGenerator.progress.processingList", BlackboardArtifact.ARTIFACT_TYPE.TSK_HASHSET_HIT.getDisplayName(), currentSet));
            }
            tableModule.addRow(Arrays.asList(new String[] { uniquePath, size, tagsList }));
        }
        progressPanel.increment();
        tableModule.endDataType();
    } catch (TskCoreException | SQLException ex) {
        errorList.add(NbBundle.getMessage(this.getClass(), "ReportGenerator.errList.failedQueryHashsetHits"));
        logger.log(Level.SEVERE, "Failed to query hashsets hits: ", ex);
    }
}

