FileHandler createFileHandlerWithTraces(String logDirectory){
    String logFilePath = Paths.get(logDirectory, LOG_FILE_NAME).toString();
    try {
        FileHandler fileHandler = new FileHandler(logFilePath, LOG_SIZE, UserPreferences.getLogFileCount());
        fileHandler.setEncoding(LOG_ENCODING);
        fileHandler.setFormatter(new Formatter() {
            @Override
            public String format(LogRecord record) {
                Throwable thrown = record.getThrown();
                String stackTrace = "";
                while (thrown != null) {
                    stackTrace += thrown.toString() + "\n";
                    for (StackTraceElement traceElem : record.getThrown().getStackTrace()) {
                        stackTrace += "\t" + traceElem.toString() + "\n";
                    }
                    thrown = thrown.getCause();
                }
                return (new Timestamp(record.getMillis())).toString() + " " + record.getSourceClassName() + " " + record.getSourceMethodName() + "\n" + record.getLevel() + ": " + this.formatMessage(record) + "\n" + stackTrace;
            }
        });
        return fileHandler;
    } catch (IOException ex) {
        throw new RuntimeException(String.format("Error initializing file handler for %s", logFilePath), ex);
    }
}

FileHandler createFileHandlerWithTraces(String logDirectory){
