    java.util.SortedMap<Integer, SortCriterion> criteriaMap = new TreeMap();
    availableProperties.forEach(prop -> {
        Integer sortRank = preferences.getInt(ResultViewerPersistence.getColumnSortRankKey(node, prop.getName()), 0);
        if (sortRank != 0) {
            final SortOrder sortOrder = preferences.getBoolean(ResultViewerPersistence.getColumnSortOrderKey(node, prop.getName()), true) ? SortOrder.ASCENDING : SortOrder.DESCENDING;
