    for (Column column : columns) {
        attributeTypeSet = column.removeTypeFromSet(attributeTypeSet);
    }
    for (BlackboardAttribute.Type type : attributeTypeSet) {
        columns.add(new AttributeColumn(type.getDisplayName(), type));
