void getDownloadPreVersion24(){
    FileManager fileManager = currentCase.getServices().getFileManager();
    List<AbstractFile> downloadsFiles;
    try {
        downloadsFiles = fileManager.findFiles(dataSource, "downloads.sqlite", "Firefox");
    } catch (TskCoreException ex) {
        String msg = NbBundle.getMessage(this.getClass(), "Firefox.getDlPre24.errMsg.errFetchFiles");
        logger.log(Level.WARNING, msg);
        this.addErrorMessage(this.getName() + ": " + msg);
        return;
    }
    if (downloadsFiles.isEmpty()) {
        logger.log(Level.INFO, "Didn't find any pre-version-24.0 Firefox download files.");
        return;
    }
    dataFound = true;
    Collection<BlackboardArtifact> bbartifacts = new ArrayList();
    int j = 0;
    for (AbstractFile downloadsFile : downloadsFiles) {
        if (downloadsFile.getSize() == 0) {
            continue;
        }
        String fileName = downloadsFile.getName();
        String temps = RAImageIngestModule.getRATempPath(currentCase, "firefox") + File.separator + fileName + j + ".db";
        int errors = 0;
        try {
            ContentUtils.writeToFile(downloadsFile, new File(temps), context::dataSourceIngestIsCancelled);
        } catch (ReadContentInputStreamException ex) {
            logger.log(Level.WARNING, String.format("Error reading Firefox download artifacts file '%s' (id=%d).", fileName, downloadsFile.getId()), ex);
            this.addErrorMessage(NbBundle.getMessage(this.getClass(), "Firefox.getHistory.errMsg.errAnalyzeFile", this.getName(), fileName));
            continue;
        } catch (IOException ex) {
            logger.log(Level.SEVERE, String.format("Error writing temp sqlite db file '%s' for Firefox download artifacts file '%s' (id=%d).", temps, fileName, downloadsFile.getId()), ex);
            this.addErrorMessage(NbBundle.getMessage(this.getClass(), "Firefox.getDlPre24.errMsg.errAnalyzeFiles", this.getName(), fileName));
            continue;
        }
        File dbFile = new File(temps);
        if (context.dataSourceIngestIsCancelled()) {
            dbFile.delete();
            break;
        }
        List<HashMap<String, Object>> tempList = this.dbConnect(temps, DOWNLOAD_QUERY);
        logger.log(Level.INFO, "{0}- Now getting downloads from {1} with {2} artifacts identified.", new Object[] { moduleName, temps, tempList.size() });
        for (HashMap<String, Object> result : tempList) {
            String source = result.get("source").toString();
            Collection<BlackboardAttribute> bbattributes = new ArrayList();
            bbattributes.add(new // NON-NLS
            BlackboardAttribute(// NON-NLS
            ATTRIBUTE_TYPE.TSK_URL, NbBundle.getMessage(this.getClass(), "Firefox.parentModuleName.noSpace"), source));
            bbattributes.add(new // NON-NLS
            BlackboardAttribute(// NON-NLS
            ATTRIBUTE_TYPE.TSK_DATETIME_ACCESSED, NbBundle.getMessage(this.getClass(), "Firefox.parentModuleName.noSpace"), (Long.valueOf(result.get("startTime").toString()))));
            String target = result.get("target").toString();
            if (target != null) {
                try {
                    String decodedTarget = URLDecoder.decode(target.replaceAll("file:///", ""), "UTF-8");
                    bbattributes.add(new BlackboardAttribute(ATTRIBUTE_TYPE.TSK_PATH, NbBundle.getMessage(this.getClass(), "Firefox.parentModuleName.noSpace"), decodedTarget));
                    long pathID = Util.findID(dataSource, decodedTarget);
                    if (pathID != -1) {
                        bbattributes.add(new BlackboardAttribute(ATTRIBUTE_TYPE.TSK_PATH_ID, NbBundle.getMessage(this.getClass(), "Firefox.parentModuleName.noSpace"), pathID));
                    }
                } catch (UnsupportedEncodingException ex) {
                    logger.log(Level.SEVERE, "Error decoding Firefox download URL in " + temps, ex);
                    errors++;
                }
            }
            bbattributes.add(new BlackboardAttribute(ATTRIBUTE_TYPE.TSK_PROG_NAME, NbBundle.getMessage(this.getClass(), "Firefox.parentModuleName.noSpace"), NbBundle.getMessage(this.getClass(), "Firefox.moduleName")));
            String domain = extractDomain(source);
            if (domain != null && domain.isEmpty() == false) {
                bbattributes.add(new // NON-NLS
                BlackboardAttribute(// NON-NLS
                ATTRIBUTE_TYPE.TSK_DOMAIN, NbBundle.getMessage(this.getClass(), "Firefox.parentModuleName.noSpace"), domain));
            }
            BlackboardArtifact bbart = this.addArtifact(ARTIFACT_TYPE.TSK_WEB_DOWNLOAD, downloadsFile, bbattributes);
            if (bbart != null) {
                bbartifacts.add(bbart);
            }
        }
        if (errors > 0) {
            this.addErrorMessage(NbBundle.getMessage(this.getClass(), "Firefox.getDlPre24.errMsg.errParsingArtifacts", this.getName(), errors));
        }
        j++;
        dbFile.delete();
        break;
    }
    services.fireModuleDataEvent(new ModuleDataEvent(NbBundle.getMessage(this.getClass(), "Firefox.parentModuleName"), BlackboardArtifact.ARTIFACT_TYPE.TSK_WEB_DOWNLOAD, bbartifacts));
}

