        manager.startUp();
        autoIngestStarted = true;
    } catch (AutoIngestManager.AutoIngestManagerException ex) {
        sysLogger.log(Level.SEVERE, "Dashboard error starting up auto ingest", ex);
        tbStatusMessage.setText(NbBundle.getMessage(AutoIngestControlPanel.class, "AutoIngestControlPanel.AutoIngestStartupError"));
        manager = null;
        JOptionPane.showMessageDialog(this, NbBundle.getMessage(AutoIngestControlPanel.class, "AutoIngestControlPanel.AutoIngestStartupFailed.Message"), NbBundle.getMessage(AutoIngestControlPanel.class, "AutoIngestControlPanel.AutoIngestStartupFailed.Title"), JOptionPane.ERROR_MESSAGE);
        bnOptions.setEnabled(true);
        return;
    }
    PropertyChangeListener propChangeListener = (PropertyChangeEvent evt) -> {
        String serviceDisplayName = ServicesMonitor.Service.valueOf(evt.getPropertyName()).toString();
        String status = evt.getNewValue().toString();
        if (status.equals(ServicesMonitor.ServiceStatus.UP.toString())) {
            status = NbBundle.getMessage(AutoIngestControlPanel.class, "AutoIngestControlPanel.tbServicesStatusMessage.Message.Up");
        } else if (status.equals(ServicesMonitor.ServiceStatus.DOWN.toString())) {
            status = NbBundle.getMessage(AutoIngestControlPanel.class, "AutoIngestControlPanel.tbServicesStatusMessage.Message.Down");
            sysLogger.log(Level.SEVERE, "Connection to {0} is down", serviceDisplayName);
        }
        if (statusByService.containsKey(serviceDisplayName) && status.equals(statusByService.get(serviceDisplayName))) {
            return;
        }
        statusByService.put(serviceDisplayName, status);
        displayServicesStatus();
    };
    Set<String> servicesList = new HashSet();
    servicesList.add(ServicesMonitor.Service.REMOTE_CASE_DATABASE.toString());
    servicesList.add(ServicesMonitor.Service.REMOTE_KEYWORD_SEARCH.toString());
    servicesList.add(ServicesMonitor.Service.MESSAGING.toString());
    ServicesMonitor.getInstance().addSubscriber(servicesList, propChangeListener);
    manager.addObserver(this);
    updateExecutor = Executors.newSingleThreadExecutor(new ThreadFactoryBuilder().setNameFormat(UPDATE_TASKS_THREAD_NAME).build());
    updateExecutor.submit(new UpdateAllJobsTablesTask());
    manager.scanInputDirsNow();
    bnPause.setText(org.openide.util.NbBundle.getMessage(AutoIngestControlPanel.class, "AutoIngestControlPanel.bnPause.text"));
    bnPause.setToolTipText(org.openide.util.NbBundle.getMessage(AutoIngestControlPanel.class, "AutoIngestControlPanel.bnPause.toolTipText"));
    bnRefresh.setEnabled(true);
    bnOptions.setEnabled(false);
    tbStatusMessage.setText(org.openide.util.NbBundle.getMessage(AutoIngestControlPanel.class, "AutoIngestControlPanel.bnPause.running"));
}

Case createCases(String[] caseNames,Path[][] caseDataSourcePaths,IngestJobSettings ingestJobSettings,String caseReferenceToStore){
    Case currentCase = null;
    if (caseNames.length != caseDataSourcePaths.length) {
        Assert.fail(new IllegalArgumentException("caseReferenceToStore should be one of the values given in the 'cases' parameter.").getMessage());
    }
    String lastCaseName = null;
    Path[] lastPathsForCase = null;
    for (int i = 0; i < caseNames.length; i++) {
        String caseName = caseNames[i];
        Path[] pathsForCase = caseDataSourcePaths[i];
        if (caseName.equals(caseReferenceToStore)) {
            lastCaseName = caseName;
            lastPathsForCase = pathsForCase;
        } else {
            this.createCase(caseName, ingestJobSettings, false, pathsForCase);
        }
    }
    if (lastCaseName != null && lastPathsForCase != null) {
        currentCase = this.createCase(lastCaseName, ingestJobSettings, true, lastPathsForCase);
    }
    if (currentCase == null) {
        Assert.fail(new IllegalArgumentException("caseReferenceToStore should be one of the values given in the 'cases' parameter.").getMessage());
        return null;
    } else {
        return currentCase;
    }
}

