void findContactsInDB(String DatabasePath,long fileId){
    if (DatabasePath == null || DatabasePath.isEmpty()) {
        return;
    }
    Case currentCase;
    try {
        currentCase = Case.getCurrentCaseThrows();
    } catch (NoCurrentCaseException ex) {
        logger.log(Level.SEVERE, "Exception while getting open case.", ex);
        return;
    }
    Statement statement = null;
    try {
        Class.forName("org.sqlite.JDBC");
        connection = DriverManager.getConnection("jdbc:sqlite:" + DatabasePath);
        statement = connection.createStatement();
    } catch (ClassNotFoundException | SQLException e) {
        logger.log(Level.SEVERE, "Error opening database", e);
    }
    SleuthkitCase skCase = currentCase.getSleuthkitCase();
    try {
        AbstractFile file = skCase.getAbstractFileById(fileId);
        if (file == null) {
            logger.log(Level.SEVERE, "Error getting abstract file {0}", fileId);
            return;
        }
        ResultSet resultSet = null;
        try {
            resultSet = // NON-NLS
            statement.executeQuery("SELECT mimetype,data1, name_raw_contact.display_name AS display_name \n" + "FROM raw_contacts JOIN contacts ON (raw_contacts.contact_id=contacts._id) \n" + "JOIN raw_contacts AS name_raw_contact ON(name_raw_contact_id=name_raw_contact._id) " + "LEFT OUTER JOIN data ON (data.raw_contact_id=raw_contacts._id) \n" + "LEFT OUTER JOIN mimetypes ON (data.mimetype_id=mimetypes._id) \n" + "WHERE mimetype = 'vnd.android.cursor.item/phone_v2' OR mimetype = 'vnd.android.cursor.item/email_v2'\n" + "ORDER BY name_raw_contact.display_name ASC;");
            BlackboardArtifact bba;
            bba = file.newArtifact(BlackboardArtifact.ARTIFACT_TYPE.TSK_CONTACT);
            Collection<BlackboardAttribute> attributes = new ArrayList();
            String name;
            String oldName = "";
            String mimetype;
            String data1;
            while (resultSet.next()) {
                name = resultSet.getString("display_name");
                data1 = resultSet.getString("data1");
                mimetype = resultSet.getString("mimetype");
                if (name.equals(oldName) == false) {
                    attributes.add(new BlackboardAttribute(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_NAME, moduleName, name));
                }
                if (mimetype.equals("vnd.android.cursor.item/phone_v2")) {
                    attributes.add(new BlackboardAttribute(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_PHONE_NUMBER, moduleName, data1));
                } else {
                    attributes.add(new BlackboardAttribute(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_EMAIL, moduleName, data1));
                }
                oldName = name;
                bba.addAttributes(attributes);
                try {
                    blackboard.indexArtifact(bba);
                } catch (Blackboard.BlackboardException ex) {
                    logger.log(Level.SEVERE, "Unable to index blackboard artifact " + bba.getArtifactID(), ex);
                    MessageNotifyUtil.Notify.error(Bundle.ContactAnalyzer_indexError_message(), bba.getDisplayName());
                }
            }
        } catch (Exception e) {
            logger.log(Level.SEVERE, "Error parsing Contacts to Blackboard", e);
        } finally {
            try {
                resultSet.close();
                statement.close();
                connection.close();
            } catch (Exception e) {
                logger.log(Level.SEVERE, "Error closing database", e);
            }
        }
    } catch (Exception e) {
        logger.log(Level.SEVERE, "Error parsing Contacts to Blackboard", e);
    }
}

