USBInfo parseAndLookup(String dev){
    String[] dtokens = dev.split("[_&]");
    String vID = dtokens[1].toUpperCase();
    String pID;
    if (dtokens.length < 4 || dtokens[3].length() < 4) {
        pID = "0000";
    } else {
        pID = dtokens[3];
    }
    pID = pID.toUpperCase();
    String key = vID + pID;
    if (devices.containsKey(key)) {
        return devices.get(key);
    }
    key = vID + "0000";
    if (devices.containsKey(key)) {
        USBInfo info = devices.get(key);
        return new USBInfo(info.getVendor(), NbBundle.getMessage(this.getClass(), "UsbDeviceIdMapper.parseAndLookup.text", pID));
    }
    return new USBInfo(null, null);
}

