        try {
            filesIndexed = KeywordSearch.getServer().queryNumIndexedFiles();
        } catch (KeywordSearchModuleException | NoOpenCoreException ignored) {
        }
    }
    if (filesIndexed == 0) {
        if (isIngestRunning) {
            KeywordSearchUtil.displayDialog(keywordSearchErrorDialogHeader, NbBundle.getMessage(this.getClass(), "AbstractKeywordSearchPerformer.search.noFilesInIdxMsg", KeywordSearchSettings.getUpdateFrequency().getTime()), KeywordSearchUtil.DIALOG_MESSAGE_TYPE.ERROR);
