        }
        return;
    }
    if (isIngestRunning) {
        if (KeywordSearchUtil.displayConfirmDialog(org.openide.util.NbBundle.getMessage(this.getClass(), "AbstractKeywordSearchPerformer.search.searchIngestInProgressTitle"), NbBundle.getMessage(this.getClass(), "AbstractKeywordSearchPerformer.search.ingestInProgressBody"), KeywordSearchUtil.DIALOG_MESSAGE_TYPE.WARN) == false) {
            return;
        }
    }
    final List<KeywordList> keywordLists = getKeywordLists();
    if (keywordLists.isEmpty()) {
