boolean retryRequest(IOException exception,int executionCount,HttpContext context,boolean retryRequest,HttpContext context){
    final HttpRequest request = (HttpRequest) context.getAttribute(ExecutionContext.HTTP_REQUEST);
    final Object obj = context.getAttribute(ExecutionContext.HTTP_REQ_SENT);
    final boolean isSent = obj == null ? false : (Boolean) obj;
    if (requestIsAborted(request)) {
        return false;
    }
    if (handleAsIdempotent(request)) {
        return true;
    }
    if (!isSent || this.requestSentRetryEnabled) {
        return true;
    }
    return false;
}

