List<String> readExtentFilesFromVmdkDescriptorFile(File file){
    List<String> extentFiles = new ArrayList();
    try (BufferedReader br = new BufferedReader(new FileReader(file))) {
        String line = br.readLine();
        while (null != line) {
            String[] splited = line.split(" ");
            if (splited.length < MIN_VMDK_EXTENT_DESCRIPTOR_FIELDS) {
                continue;
            }
            if (splited[0].equals("RW") || splited[0].equals("RDONLY") || splited[0].equals("NOACCESS")) {
                String extentFileName = splited[FILE_NAME_FIELD_INDX].replace("\"", "");
                extentFiles.add(extentFileName);
            }
            line = br.readLine();
        }
    } catch (Exception ex) {
        logger.log(Level.WARNING, String.format("Error while parsing vmdk descriptor file %s", file.toString()), ex);
    }
    return extentFiles;
}

