void findTextsInDB(String DatabasePath,long fileId){
    if (DatabasePath == null || DatabasePath.isEmpty()) {
        return;
    }
    Case currentCase;
    try {
        currentCase = Case.getCurrentCaseThrows();
    } catch (NoCurrentCaseException ex) {
        logger.log(Level.SEVERE, "Exception while getting open case.", ex);
        return;
    }
    try {
        Class.forName("org.sqlite.JDBC");
        connection = DriverManager.getConnection("jdbc:sqlite:" + DatabasePath);
        statement = connection.createStatement();
    } catch (ClassNotFoundException | SQLException e) {
        logger.log(Level.SEVERE, "Error opening database", e);
    }
    SleuthkitCase skCase = currentCase.getSleuthkitCase();
    try {
        AbstractFile file = skCase.getAbstractFileById(fileId);
        if (file == null) {
            logger.log(Level.SEVERE, "Error getting abstract file {0}", fileId);
            return;
        }
        try {
            resultSet = // NON-NLS
            statement.executeQuery("SELECT address,date,type,subject,body FROM sms;");
            BlackboardArtifact bba;
            String address;
            String date;
            String type;
            String subject;
            String body;
            while (resultSet.next()) {
                address = resultSet.getString("address");
                date = resultSet.getString("date");
                type = resultSet.getString("type");
                subject = resultSet.getString("subject");
                body = resultSet.getString("body");
                bba = file.newArtifact(BlackboardArtifact.ARTIFACT_TYPE.TSK_MESSAGE);
                Collection<BlackboardAttribute> attributes = new ArrayList();
                if (type.equals("1")) {
                    attributes.add(new BlackboardAttribute(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_DIRECTION, moduleName, NbBundle.getMessage(this.getClass(), "TextMessageAnalyzer.bbAttribute.incoming")));
                    attributes.add(new BlackboardAttribute(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_PHONE_NUMBER_FROM, moduleName, address));
                } else {
                    attributes.add(new BlackboardAttribute(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_DIRECTION, moduleName, NbBundle.getMessage(this.getClass(), "TextMessageAnalyzer.bbAttribute.outgoing")));
                    attributes.add(new BlackboardAttribute(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_PHONE_NUMBER_TO, moduleName, address));
                }
                attributes.add(new BlackboardAttribute(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_DATETIME, moduleName, date));
                attributes.add(new BlackboardAttribute(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_DIRECTION, moduleName, type));
                attributes.add(new BlackboardAttribute(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_SUBJECT, moduleName, subject));
                attributes.add(new BlackboardAttribute(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_TEXT, moduleName, body));
                attributes.add(new BlackboardAttribute(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_MESSAGE_TYPE, moduleName, NbBundle.getMessage(this.getClass(), "TextMessageAnalyzer.bbAttribute.smsMessage")));
                bba.addAttributes(attributes);
                try {
                    blackboard.indexArtifact(bba);
                } catch (Blackboard.BlackboardException ex) {
                    logger.log(Level.SEVERE, "Unable to index blackboard artifact " + bba.getArtifactID(), ex);
                    MessageNotifyUtil.Notify.error(Bundle.TextMessageAnalyzer_indexError_message(), bba.getDisplayName());
                }
            }
        } catch (Exception e) {
            logger.log(Level.SEVERE, "Error parsing text messages to Blackboard", e);
        } finally {
            try {
                resultSet.close();
                statement.close();
                connection.close();
            } catch (Exception e) {
                logger.log(Level.SEVERE, "Error closing database", e);
            }
        }
    } catch (Exception e) {
        logger.log(Level.SEVERE, "Error parsing text messages to Blackboard", e);
    }
}

void getDownloadVersion24(){
    FileManager fileManager = currentCase.getServices().getFileManager();
    List<AbstractFile> downloadsFiles;
    try {
        downloadsFiles = fileManager.findFiles(dataSource, "places.sqlite", "Firefox");
    } catch (TskCoreException ex) {
        String msg = NbBundle.getMessage(this.getClass(), "Firefox.getDlV24.errMsg.errFetchFiles");
        logger.log(Level.WARNING, msg);
        this.addErrorMessage(this.getName() + ": " + msg);
        return;
    }
    if (downloadsFiles.isEmpty()) {
        logger.log(Level.INFO, "Didn't find any version-24.0 Firefox download files.");
        return;
    }
    dataFound = true;
    Collection<BlackboardArtifact> bbartifacts = new ArrayList();
    int j = 0;
    for (AbstractFile downloadsFile : downloadsFiles) {
        if (downloadsFile.getSize() == 0) {
            continue;
        }
        String fileName = downloadsFile.getName();
        String temps = RAImageIngestModule.getRATempPath(currentCase, "firefox") + File.separator + fileName + "-downloads" + j + ".db";
        int errors = 0;
        try {
            ContentUtils.writeToFile(downloadsFile, new File(temps), context::dataSourceIngestIsCancelled);
        } catch (ReadContentInputStreamException ex) {
            logger.log(Level.WARNING, String.format("Error reading Firefox download artifacts file '%s' (id=%d).", fileName, downloadsFile.getId()), ex);
            this.addErrorMessage(NbBundle.getMessage(this.getClass(), "Firefox.getHistory.errMsg.errAnalyzeFile", this.getName(), fileName));
            continue;
        } catch (IOException ex) {
            logger.log(Level.SEVERE, String.format("Error writing temp sqlite db file '%s' for Firefox download artifacts file '%s' (id=%d).", temps, fileName, downloadsFile.getId()), ex);
            this.addErrorMessage(NbBundle.getMessage(this.getClass(), "Firefox.getDlV24.errMsg.errAnalyzeFile", this.getName(), fileName));
            continue;
        }
        File dbFile = new File(temps);
        if (context.dataSourceIngestIsCancelled()) {
            dbFile.delete();
            break;
        }
        List<HashMap<String, Object>> tempList = this.dbConnect(temps, DOWNLOAD_QUERY_V24);
        logger.log(Level.INFO, "{0} - Now getting downloads from {1} with {2} artifacts identified.", new Object[] { moduleName, temps, tempList.size() });
        for (HashMap<String, Object> result : tempList) {
            String url = result.get("url").toString();
            Collection<BlackboardAttribute> bbattributes = new ArrayList();
            bbattributes.add(new // NON-NLS
            BlackboardAttribute(// NON-NLS
            ATTRIBUTE_TYPE.TSK_URL, NbBundle.getMessage(this.getClass(), "Firefox.parentModuleName.noSpace"), url));
            String target = result.get("target").toString();
            if (target != null) {
                try {
                    String decodedTarget = URLDecoder.decode(target.replaceAll("file:///", ""), "UTF-8");
                    bbattributes.add(new BlackboardAttribute(ATTRIBUTE_TYPE.TSK_PATH, NbBundle.getMessage(this.getClass(), "Firefox.parentModuleName.noSpace"), decodedTarget));
                    long pathID = Util.findID(dataSource, decodedTarget);
                    if (pathID != -1) {
                        bbattributes.add(new BlackboardAttribute(ATTRIBUTE_TYPE.TSK_PATH_ID, NbBundle.getMessage(this.getClass(), "Firefox.parentModuleName.noSpace"), pathID));
                    }
                } catch (UnsupportedEncodingException ex) {
                    logger.log(Level.SEVERE, "Error decoding Firefox download URL in " + temps, ex);
                    errors++;
                }
            }
            bbattributes.add(new // NON-NLS
            BlackboardAttribute(// NON-NLS
            ATTRIBUTE_TYPE.TSK_DATETIME_ACCESSED, NbBundle.getMessage(this.getClass(), "Firefox.parentModuleName.noSpace"), Long.valueOf(result.get("lastModified").toString())));
            bbattributes.add(new BlackboardAttribute(ATTRIBUTE_TYPE.TSK_PROG_NAME, NbBundle.getMessage(this.getClass(), "Firefox.parentModuleName.noSpace"), NbBundle.getMessage(this.getClass(), "Firefox.moduleName")));
            String domain = extractDomain(url);
            if (domain != null && domain.isEmpty() == false) {
                bbattributes.add(new // NON-NLS
                BlackboardAttribute(// NON-NLS
                ATTRIBUTE_TYPE.TSK_DOMAIN, NbBundle.getMessage(this.getClass(), "Firefox.parentModuleName.noSpace"), domain));
            }
            BlackboardArtifact bbart = this.addArtifact(ARTIFACT_TYPE.TSK_WEB_DOWNLOAD, downloadsFile, bbattributes);
            if (bbart != null) {
                bbartifacts.add(bbart);
            }
        }
        if (errors > 0) {
            this.addErrorMessage(NbBundle.getMessage(this.getClass(), "Firefox.getDlV24.errMsg.errParsingArtifacts", this.getName(), errors));
        }
        j++;
        dbFile.delete();
        break;
    }
    services.fireModuleDataEvent(new ModuleDataEvent(NbBundle.getMessage(this.getClass(), "Firefox.parentModuleName"), BlackboardArtifact.ARTIFACT_TYPE.TSK_WEB_DOWNLOAD, bbartifacts));
}

