HashSet<String> getModulesNames(String executionContext,String propertyName,String defaultSetting){
    if (ModuleSettings.settingExists(executionContext, propertyName) == false) {
        ModuleSettings.setConfigSetting(executionContext, propertyName, defaultSetting);
    }
    HashSet<String> moduleNames = new HashSet();
    String modulesSetting = ModuleSettings.getConfigSetting(executionContext, propertyName);
    if (!modulesSetting.isEmpty()) {
        String[] settingNames = modulesSetting.split(", ");
        for (String name : settingNames) {
            switch(name) {
                case "Thunderbird Parser":
                case "MBox Parser":
                    moduleNames.add("Email Parser");
                    break;
                case "File Extension Mismatch Detection":
                    moduleNames.add("Extension Mismatch Detector");
                    break;
                case "EWF Verify":
                case "E01 Verify":
                case "E01 Verifier":
                    moduleNames.add("Data Source Integrity");
                    break;
                case "Archive Extractor":
                    moduleNames.add("Embedded File Extractor");
                    break;
                default:
                    moduleNames.add(name);
            }
        }
    }
    return moduleNames;
}

