String displayNameToUniqueName(String caseDisplayName){
    String uniqueCaseName = caseDisplayName.replaceAll("[^\\p{ASCII}]", "_");
    uniqueCaseName = uniqueCaseName.replaceAll("[\\p{Cntrl}]", "_");
    uniqueCaseName = uniqueCaseName.replaceAll("[ /?:'\"\\\\]", "_");
    uniqueCaseName = uniqueCaseName.toLowerCase();
    SimpleDateFormat dateFormat = new SimpleDateFormat("yyyyMMdd_HHmmss");
    Date date = new Date();
    uniqueCaseName = uniqueCaseName + "_" + dateFormat.format(date);
    return uniqueCaseName;
}

