void endJob(long jobId){
    SearchJobInfo job;
    boolean readyForFinalSearch = false;
    job = jobs.get(jobId);
    if (job == null) {
        return;
    }
    if (job.decrementModuleReferenceCount() == 0) {
        jobs.remove(jobId);
        readyForFinalSearch = true;
    }
    if (readyForFinalSearch) {
        logger.log(Level.INFO, "Commiting search index before final search for search job {0}", job.getJobId());
        commit();
        doFinalSearch(job);
        if (jobs.isEmpty()) {
            logger.log(Level.INFO, "No more search jobs. Stopping periodic search task");
            periodicSearchTaskRunning = false;
            jobProcessingTaskFuture.cancel(true);
        }
    }
}

void findContacts(IngestJobContext context){
    Case openCase;
    try {
        openCase = Case.getCurrentCaseThrows();
    } catch (NoCurrentCaseException ex) {
        logger.log(Level.SEVERE, "Exception while getting open case.", ex);
        return;
    }
    blackboard = openCase.getServices().getBlackboard();
    List<AbstractFile> absFiles;
    try {
        SleuthkitCase skCase = openCase.getSleuthkitCase();
        absFiles = skCase.findAllFilesWhere("LOWER(name) LIKE LOWER('ll_history%') ");
        if (absFiles.isEmpty()) {
            return;
        }
        for (AbstractFile file : absFiles) {
            try {
                jFile = new java.io.File(openCase.getTempDirectory(), file.getName().replaceAll("[<>%|\"/:*\\\\]", ""));
                dbPath = jFile.toString();
                fileId = file.getId();
                ContentUtils.writeToFile(file, jFile, context::dataSourceIngestIsCancelled);
            } catch (ReadContentInputStreamException ex) {
                logger.log(Level.WARNING, String.format("Error reading content from file '%s' (id=%d).", file.getName(), fileId), ex);
            } catch (Exception ex) {
                logger.log(Level.SEVERE, String.format("Error writing content from file '%s' (id=%d) to '%s'.", file.getName(), fileId, dbPath), ex);
            }
        }
    } catch (TskCoreException e) {
        logger.log(Level.SEVERE, "Error finding Contacts", e);
    }
}

List<String> getAvailableLanguages(){
    List<String> languages = new ArrayList();
    LocaleListCompat localeList = LocaleListCompat.getDefault();
    for (int i = 0; i < localeList.size(); i++) {
        String languageCode = localeToWikiLanguageCode(localeList.get(i));
        if (!languages.contains(languageCode)) {
            languages.add(languageCode);
        }
    }
    if (languages.isEmpty()) {
        languages.add(localeToWikiLanguageCode(Locale.getDefault()));
    }
    InputMethodManager imm = (InputMethodManager) WikipediaApp.getInstance().getSystemService(Context.INPUT_METHOD_SERVICE);
    if (imm != null) {
        List<InputMethodInfo> ims = imm.getEnabledInputMethodList();
        if (ims == null) {
            ims = Collections.emptyList();
        }
        List<String> langTagList = new ArrayList();
        for (InputMethodInfo method : ims) {
            List<InputMethodSubtype> submethods = imm.getEnabledInputMethodSubtypeList(method, true);
            if (submethods == null) {
                submethods = Collections.emptyList();
            }
            for (InputMethodSubtype submethod : submethods) {
                if (submethod.getMode().equals("keyboard")) {
                    String langTag = Build.VERSION.SDK_INT >= Build.VERSION_CODES.N && !TextUtils.isEmpty(submethod.getLanguageTag()) ? submethod.getLanguageTag() : submethod.getLocale();
                    if (TextUtils.isEmpty(langTag)) {
                        continue;
                    }
                    if (langTag.contains("_")) {
                        langTag = langTag.replace('_', '-');
                    }
                    if (!langTagList.contains(langTag)) {
                        langTagList.add(langTag);
                    }
                    if (langTag.toLowerCase().equals(AppLanguageLookUpTable.CHINESE_CN_LANGUAGE_CODE) && !langTagList.contains("zh-TW")) {
                        langTagList.add("zh-TW");
                    }
                }
            }
        }
        if (!langTagList.isEmpty()) {
            localeList = LocaleListCompat.forLanguageTags(StringUtil.listToCsv(langTagList));
            for (int i = 0; i < localeList.size(); i++) {
                String langCode = localeToWikiLanguageCode(localeList.get(i));
                if (!TextUtils.isEmpty(langCode) && !languages.contains(langCode) && !langCode.equals("und")) {
                    languages.add(langCode);
                }
            }
        }
    }
    return languages;
}

void pause(){
    if (State.RUNNING != state) {
