            outputStatement.executeUpdate("INSERT INTO content_tags (tag_id, obj_id, tag_name_id, comment, begin_byte_offset, end_byte_offset, examiner_id) VALUES (" + value + "," + inputResultSet.getLong(2) + "," + inputResultSet.getLong(3) + ",'" + inputResultSet.getString(4) + "'," + inputResultSet.getLong(5) + "," + inputResultSet.getLong(6) + "," + inputResultSet.getInt(7) + ")");
        } catch (SQLException ex) {
            if (ex.getErrorCode() != 0) {
                throw new SQLException(ex);
            }
        }
    }
    numberingPK = postgreSQLConnection.createStatement();
    numberingPK.execute("ALTER SEQUENCE content_tags_tag_id_seq RESTART WITH " + (biggestPK + 1));
    biggestPK = 0;
    inputStatement = sqliteConnection.createStatement();
    inputResultSet = inputStatement.executeQuery("SELECT * FROM blackboard_artifact_tags");
    while (inputResultSet.next()) {
        outputStatement = postgreSQLConnection.createStatement();
        try {
            long value = inputResultSet.getLong(1);
            if (value > biggestPK) {
                biggestPK = value;
            }
            // NON-NLS
            outputStatement.executeUpdate("INSERT INTO blackboard_artifact_tags (tag_id, artifact_id, tag_name_id, comment) VALUES (" + value + "," + inputResultSet.getLong(2) + "," + inputResultSet.getLong(3) + ",'" + inputResultSet.getString(4) + "','" + inputResultSet.getString(5) + "')");
        } catch (SQLException ex) {
            if (ex.getErrorCode() != 0) {
                throw new SQLException(ex);
            }
        }
    }
    numberingPK = postgreSQLConnection.createStatement();
    numberingPK.execute("ALTER SEQUENCE blackboard_artifact_tags_tag_id_seq RESTART WITH " + (biggestPK + 1));
    sqliteConnection.close();
    postgreSQLConnection.close();
}

