                        }
                    } catch (TskCoreException ex) {
                        LOGGER.log(Level.SEVERE, "Error getting hashset hit info for event.", ex);
                    }
                }
                String hashSetCountsString = hashSetCounts.entrySet().stream().map((Map.Entry<String, Long> t) -> t.getKey() + " : " + t.getValue()).collect(Collectors.joining("\n"));
                Map<String, Long> tagCounts = new HashMap();
