boolean makeConfigFile(String moduleName){
    if (!configExists(moduleName)) {
        Path propPath = getPropertyPath(moduleName);
        Path parent = propPath.getParent();
        Properties props = new Properties();
        try {
            if (!Files.exists(parent)) {
                Files.createDirectories(parent);
            }
            Files.createFile(propPath);
            try (OutputStream fos = Files.newOutputStream(propPath)) {
                props.store(fos, "");
            }
        } catch (IOException e) {
            Logger.getLogger(PerCaseProperties.class.getName()).log(Level.WARNING, "Was not able to create a new properties file.", e);
            return false;
        }
        return true;
    }
    return false;
}

