boolean isValidRuleDefinition(){
    if (!(this.mimeCheck.isSelected() || this.fileSizeCheck.isSelected() || this.pathCheck.isSelected() || this.nameCheck.isSelected() || this.dateCheck.isSelected())) {
        NotifyDescriptor notifyDesc = new NotifyDescriptor.Message(Bundle.FilesSetRulePanel_NoConditionError(), NotifyDescriptor.WARNING_MESSAGE);
        DialogDisplayer.getDefault().notify(notifyDesc);
        return false;
    }
    if (this.nameCheck.isSelected()) {
        if (this.nameTextField.getText().isEmpty()) {
            NotifyDescriptor notifyDesc = new NotifyDescriptor.Message(Bundle.FilesSetRulePanel_NoNameError(), NotifyDescriptor.WARNING_MESSAGE);
            DialogDisplayer.getDefault().notify(notifyDesc);
            return false;
        }
        if (this.nameRegexCheckbox.isSelected()) {
            try {
                Pattern.compile(this.nameTextField.getText());
            } catch (PatternSyntaxException ex) {
                NotifyDescriptor notifyDesc = new NotifyDescriptor.Message(NbBundle.getMessage(FilesSetPanel.class, "FilesSetRulePanel.messages.invalidNameRegex", ex.getLocalizedMessage()), NotifyDescriptor.WARNING_MESSAGE);
                DialogDisplayer.getDefault().notify(notifyDesc);
                return false;
            }
        } else if (this.nameTextField.getText().isEmpty() || !FilesSetRulePanel.containsOnlyLegalChars(this.nameTextField.getText(), FilesSetRulePanel.ILLEGAL_FILE_NAME_CHARS)) {
            NotifyDescriptor notifyDesc = new NotifyDescriptor.Message(NbBundle.getMessage(FilesSetPanel.class, "FilesSetRulePanel.messages.invalidCharInName"), NotifyDescriptor.WARNING_MESSAGE);
            DialogDisplayer.getDefault().notify(notifyDesc);
            return false;
        }
    }
    if (this.pathCheck.isSelected()) {
        if (this.pathTextField.getText().isEmpty()) {
            NotifyDescriptor notifyDesc = new NotifyDescriptor.Message(Bundle.FilesSetRulePanel_NoPathError(), NotifyDescriptor.WARNING_MESSAGE);
            DialogDisplayer.getDefault().notify(notifyDesc);
            return false;
        }
        if (this.pathRegexCheckBox.isSelected()) {
            try {
                Pattern.compile(this.pathTextField.getText());
            } catch (PatternSyntaxException ex) {
                NotifyDescriptor notifyDesc = new NotifyDescriptor.Message(NbBundle.getMessage(FilesSetPanel.class, "FilesSetRulePanel.messages.invalidPathRegex", ex.getLocalizedMessage()), NotifyDescriptor.WARNING_MESSAGE);
                DialogDisplayer.getDefault().notify(notifyDesc);
                return false;
            }
        } else if (this.pathTextField.getText().isEmpty() || !FilesSetRulePanel.containsOnlyLegalChars(this.pathTextField.getText(), FilesSetRulePanel.ILLEGAL_FILE_PATH_CHARS)) {
            NotifyDescriptor notifyDesc = new NotifyDescriptor.Message(NbBundle.getMessage(FilesSetPanel.class, "FilesSetRulePanel.messages.invalidCharInPath"), NotifyDescriptor.WARNING_MESSAGE);
            DialogDisplayer.getDefault().notify(notifyDesc);
            return false;
        }
    }
    if (this.mimeCheck.isSelected()) {
        if (this.mimeTypeComboBox.getSelectedIndex() == 0) {
            NotifyDescriptor notifyDesc = new NotifyDescriptor.Message(Bundle.FilesSetRulePanel_NoMimeTypeError(), NotifyDescriptor.WARNING_MESSAGE);
            DialogDisplayer.getDefault().notify(notifyDesc);
            return false;
        }
    }
    if (this.fileSizeCheck.isSelected()) {
        if ((Integer) this.fileSizeSpinner.getValue() == 0 && !((String) this.equalitySymbolComboBox.getSelectedItem()).equals("=")) {
            NotifyDescriptor notifyDesc = new NotifyDescriptor.Message(Bundle.FilesSetRulePanel_ZeroFileSizeError(), NotifyDescriptor.WARNING_MESSAGE);
            DialogDisplayer.getDefault().notify(notifyDesc);
            return false;
        }
    }
    if (this.dateCheck.isSelected()) {
        if (this.daysIncludedTextField.getText().isEmpty()) {
            NotifyDescriptor notifyDesc = new NotifyDescriptor.Message(Bundle.FilesSetRulePanel_DaysIncludedEmptyError(), NotifyDescriptor.WARNING_MESSAGE);
            DialogDisplayer.getDefault().notify(notifyDesc);
            return false;
        }
        try {
            int value = Integer.parseInt(daysIncludedTextField.getText());
            if (value < 0) {
                throw new NumberFormatException("Negative numbers are not allowed for the within N days condition");
            }
        } catch (NumberFormatException e) {
            NotifyDescriptor notifyDesc = new NotifyDescriptor.Message(Bundle.FilesSetRulePanel_DaysIncludedInvalidError(), NotifyDescriptor.WARNING_MESSAGE);
            DialogDisplayer.getDefault().notify(notifyDesc);
            return false;
        }
    }
    return true;
