void addRowWithTaggedContentHyperlink(List<String> row,ContentTag contentTag){
    Content content = contentTag.getContent();
    if (content instanceof AbstractFile == false) {
        addRow(row, true);
        return;
    }
    AbstractFile file = (AbstractFile) content;
    StringBuilder localFileLink = new StringBuilder();
    if (!(file.isDir() || file.getType() == TSK_DB_FILES_TYPE_ENUM.UNALLOC_BLOCKS || file.getType() == TSK_DB_FILES_TYPE_ENUM.UNUSED_BLOCKS)) {
        localFileLink.append("<a href=\"");
        String localFilePath = saveContent(file, contentTag.getName().getDisplayName());
        localFileLink.append(localFilePath);
        localFileLink.append("\" target=\"_top\">");
    }
    StringBuilder builder = new StringBuilder();
    builder.append("\t<tr>\n");
    int positionCounter = 0;
    for (String cell : row) {
        switch(positionCounter) {
            case 1:
                builder.append("\t\t<td class=\"left_align_cell\">").append(localFileLink.toString()).append(cell).append("</a></td>\n");
                break;
            case 7:
                builder.append("\t\t<td class=\"right_align_cell\">").append(cell).append("</td>\n");
                break;
            default:
                builder.append("\t\t<td>").append(cell).append("</td>\n");
                break;
        }
        ++positionCounter;
    }
    builder.append("\t</tr>\n");
    rowCount++;
    try {
        out.write(builder.toString());
    } catch (IOException ex) {
        logger.log(Level.SEVERE, "Failed to write row to out.", ex);
    } catch (NullPointerException ex) {
        logger.log(Level.SEVERE, "Output writer is null. Page was not initialized before writing.", ex);
    }
}

