        result.SetStyle(root.getChildText("Content"));
        result.SetLastModifyTime(DateUtil.parseRfc822Date(root.getChildText("LastModifyTime")));
        result.SetCreationDate(DateUtil.parseRfc822Date(root.getChildText("CreateTime")));
