void setServiceStatus(String service,String status,String details){
    if (statusByService.containsKey(service) && status.equals(statusByService.get(service))) {
        return;
    }
    String serviceDisplayName;
    try {
        serviceDisplayName = ServicesMonitor.Service.valueOf(service).getDisplayName();
    } catch (IllegalArgumentException ignore) {
        serviceDisplayName = service;
    }
    if (status.equals(ServiceStatus.UP.toString())) {
        logger.log(Level.INFO, "Connection to {0} is up", serviceDisplayName);
        MessageNotifyUtil.Notify.info(NbBundle.getMessage(ServicesMonitor.class, "ServicesMonitor.restoredService.notify.title"), NbBundle.getMessage(ServicesMonitor.class, "ServicesMonitor.restoredService.notify.msg", serviceDisplayName));
    } else if (status.equals(ServiceStatus.DOWN.toString())) {
        logger.log(Level.SEVERE, "Failed to connect to {0}. Reason: {1}", new Object[] { serviceDisplayName, details });
        MessageNotifyUtil.Notify.error(NbBundle.getMessage(ServicesMonitor.class, "ServicesMonitor.failedService.notify.title"), NbBundle.getMessage(ServicesMonitor.class, "ServicesMonitor.failedService.notify.msg", serviceDisplayName));
    } else {
        logger.log(Level.INFO, "Status for {0} is {1}", new Object[] { serviceDisplayName, status });
        MessageNotifyUtil.Notify.info(NbBundle.getMessage(ServicesMonitor.class, "ServicesMonitor.statusChange.notify.title"), NbBundle.getMessage(ServicesMonitor.class, "ServicesMonitor.statusChange.notify.msg", new Object[] { serviceDisplayName, status }));
    }
    statusByService.put(service, status);
    eventPublisher.publishLocally(new ServiceEvent(service, status, details));
}

