void onDraw(Canvas canvas){
    if (canvas != null) {
        super.onDraw(canvas);
    }
    mTotalWidth = 0;
    final int height = getHeight();
    if (mBgPadding == null) {
        mBgPadding = new Rect(0, 0, 0, 0);
        if (getBackground() != null) {
            getBackground().getPadding(mBgPadding);
        }
        mDivider.setBounds(0, 0, mDivider.getIntrinsicWidth(), mDivider.getIntrinsicHeight());
    }
    final int dividerYOffset = (height - mDivider.getMinimumHeight()) / 2;
    final int count = mSuggestions.size();
    final Rect bgPadding = mBgPadding;
    final Paint paint = mPaint;
    final int touchX = mTouchX;
    final int scrollX = getScrollX();
    final boolean scrolled = mScrolled;
    final boolean typedWordValid = mTypedWordValid;
    int x = 0;
    for (int i = 0; i < count; i++) {
        CharSequence suggestion = mSuggestions.get(i);
        if (suggestion == null) {
            continue;
        }
        final int wordLength = suggestion.length();
        paint.setColor(mColorNormal);
        if (mHaveMinimalSuggestion && ((i == 1 && !typedWordValid) || (i == 0 && typedWordValid))) {
            paint.setTypeface(Typeface.DEFAULT_BOLD);
            paint.setColor(mColorRecommended);
        } else if (i != 0 || (wordLength == 1 && count > 1)) {
            paint.setColor(mColorOther);
        }
        int wordWidth;
        if ((wordWidth = mWordWidth[i]) == 0) {
            float textWidth = paint.measureText(suggestion, 0, wordLength);
            wordWidth = (int) (textWidth + mHorizontalGap * 2);
            mWordWidth[i] = wordWidth;
        }
        mWordX[i] = x;
        if (touchX != OUT_OF_BOUNDS_X_CORD && !scrolled && touchX + scrollX >= x && touchX + scrollX < x + wordWidth) {
            if (canvas != null && !mShowingAddToDictionary) {
                canvas.translate(x, 0);
                mSelectionHighlight.setBounds(0, bgPadding.top, wordWidth, height);
                mSelectionHighlight.draw(canvas);
                canvas.translate(-x, 0);
            }
            mSelectedString = suggestion;
            mSelectedIndex = i;
        }
        if (canvas != null) {
            if (mAlwaysUseDrawText) {
                final int y = (int) (height + paint.getTextSize() - paint.descent()) / 2;
                canvas.drawText(suggestion, 0, wordLength, x + wordWidth / 2, y, paint);
            } else {
                final int y = (int) (height - paint.getTextSize() + paint.descent()) / 2;
                float textX = x + (wordWidth / 2) - mHorizontalGap;
                float textY = y - bgPadding.bottom - bgPadding.top;
                canvas.translate(textX, textY);
                mTextPaint.setTypeface(paint.getTypeface());
                mTextPaint.setColor(paint.getColor());
                StaticLayout suggestionText = new StaticLayout(suggestion, mTextPaint, wordWidth, Alignment.ALIGN_CENTER, 1.0f, 0.0f, false);
                suggestionText.draw(canvas);
                canvas.translate(-textX, -textY);
            }
            paint.setColor(mColorOther);
            canvas.translate(x + wordWidth, 0);
            if (count > 1 && (!mShowingAddToDictionary) && i != (count - 1)) {
                canvas.translate(0, dividerYOffset);
                mDivider.draw(canvas);
                canvas.translate(0, -dividerYOffset);
            }
            canvas.translate(-x - wordWidth, 0);
        }
        paint.setTypeface(Typeface.DEFAULT);
        x += wordWidth;
    }
    mTotalWidth = x;
    if (mTargetScrollX != scrollX) {
        scrollToTarget();
    }
}

void openFile(String mimeType,String ext,File file){
    String exePath = ExternalViewerRulesManager.getInstance().getExePathForName(mimeType);
    if (exePath.equals("")) {
        exePath = ExternalViewerRulesManager.getInstance().getExePathForName(ext);
    }
    if (!exePath.equals("")) {
        Runtime runtime = Runtime.getRuntime();
        String[] execArray = new String[] { exePath, file.getAbsolutePath() };
        try {
            runtime.exec(execArray);
        } catch (IOException ex) {
            logger.log(Level.WARNING, "Could not open the specified viewer for the given file: " + file.getName(), ex);
            JOptionPane.showMessageDialog(WindowManager.getDefault().getMainWindow(), Bundle.ExternalViewerAction_actionPerformed_failure_IO_message(), Bundle.ExternalViewerAction_actionPerformed_failure_title(), JOptionPane.ERROR_MESSAGE);
        }
    } else {
        try {
            String localpath = file.getPath();
            if (localpath.toLowerCase().contains("http")) {
                String url_path = file.getPath().replaceAll("\\\\", "/");
                Desktop.getDesktop().browse(new URI(url_path.replaceFirst("/", "//")));
            } else {
                Desktop.getDesktop().open(file);
            }
        } catch (IOException ex) {
            logger.log(Level.WARNING, "Could not find a viewer for the given file: " + file.getName(), ex);
            JOptionPane.showMessageDialog(WindowManager.getDefault().getMainWindow(), Bundle.ExternalViewerAction_actionPerformed_failure_IO_message(), Bundle.ExternalViewerAction_actionPerformed_failure_title(), JOptionPane.ERROR_MESSAGE);
        } catch (UnsupportedOperationException ex) {
            logger.log(Level.WARNING, "Platform cannot open " + file.getName() + " in the defined editor.", ex);
            JOptionPane.showMessageDialog(WindowManager.getDefault().getMainWindow(), Bundle.ExternalViewerAction_actionPerformed_failure_support_message(), Bundle.ExternalViewerAction_actionPerformed_failure_title(), JOptionPane.ERROR_MESSAGE);
        } catch (IllegalArgumentException ex) {
            logger.log(Level.WARNING, "Could not find the given file: " + file.getName(), ex);
            JOptionPane.showMessageDialog(WindowManager.getDefault().getMainWindow(), Bundle.ExternalViewerAction_actionPerformed_failure_missingFile_message(), Bundle.ExternalViewerAction_actionPerformed_failure_title(), JOptionPane.ERROR_MESSAGE);
        } catch (SecurityException ex) {
            logger.log(Level.WARNING, "Could not get permission to open the given file: " + file.getName(), ex);
            JOptionPane.showMessageDialog(WindowManager.getDefault().getMainWindow(), Bundle.ExternalViewerAction_actionPerformed_failure_permission_message(), Bundle.ExternalViewerAction_actionPerformed_failure_title(), JOptionPane.ERROR_MESSAGE);
        } catch (URISyntaxException ex) {
            logger.log(Level.WARNING, "Could not open URL provided: " + file.getPath(), ex);
            JOptionPane.showMessageDialog(null, Bundle.ExternalViewerAction_actionPerformed_failure_open_url(), Bundle.ExternalViewerAction_actionPerformed_failure_title(), JOptionPane.ERROR_MESSAGE);
        }
    }
}

