                cb.failure(call, RetrofitException.unexpectedError(new RuntimeException("Received unrecognized description edit response")));
            }
        }
        @Override
        public void onFailure(@NonNull Call<MwPostResponse> call, @NonNull Throwable t) {
            if (call.isCanceled()) {
                return;
            }
            if (t instanceof MwException) {
                handleError(call, (MwException) t, cb);
            } else {
                cb.failure(call, t);
            }
        }
    });
    return call;
}

