Set<TagNameDefinition> upgradeTagPropertiesFile(List<String> tagProperties,Set<String> standardTagsToBeCreated){
    Set<TagNameDefinition> tagNames = new LinkedHashSet();
    List<String> legacyNotableTags = new ArrayList();
    String badTagsStr = ModuleSettings.getConfigSetting("CentralRepository", "db.badTags");
    if (badTagsStr == null || badTagsStr.isEmpty()) {
        legacyNotableTags.addAll(STANDARD_NOTABLE_TAG_DISPLAY_NAMES);
    } else {
        legacyNotableTags.addAll(Arrays.asList(badTagsStr.split(",")));
    }
    for (String tagNameTuple : tagProperties) {
        String[] tagNameAttributes = tagNameTuple.split(",");
        standardTagsToBeCreated.remove(tagNameAttributes[0]);
        if (legacyNotableTags.contains(tagNameAttributes[0])) {
            tagNames.add(new TagNameDefinition(tagNameAttributes[0], tagNameAttributes[1], TagName.HTML_COLOR.valueOf(tagNameAttributes[2]), TskData.FileKnown.BAD));
        } else {
            tagNames.add(new // add the default value for that tag
            TagNameDefinition(// add the default value for that tag
            tagNameAttributes[0], // add the default value for that tag
            tagNameAttributes[1], TagName.HTML_COLOR.valueOf(tagNameAttributes[2]), TskData.FileKnown.UNKNOWN));
        }
    }
    return tagNames;
}

