void populateHashMap(){
    String query = "SELECT mime_type, count(*) AS count FROM tsk_files " + " WHERE mime_type IS NOT null " + " AND " + createBaseWhereExpr() + " GROUP BY mime_type";
    synchronized (existingMimeTypeCounts) {
        existingMimeTypeCounts.clear();
        if (skCase == null) {
            return;
        }
        try (SleuthkitCase.CaseDbQuery dbQuery = skCase.executeQuery(query)) {
            ResultSet resultSet = dbQuery.getResultSet();
            while (resultSet.next()) {
                final String mime_type = resultSet.getString("mime_type");
                if (!mime_type.isEmpty()) {
                    final String mediaType = StringUtils.substringBefore(mime_type, "/");
                    final String subType = StringUtils.removeStart(mime_type, mediaType + "/");
                    if (!mediaType.isEmpty() && !subType.isEmpty()) {
                        final long count = resultSet.getLong("count");
                        existingMimeTypeCounts.computeIfAbsent(mediaType, t -> new HashMap()).put(subType, count);
                    }
                }
            }
        } catch (TskCoreException | SQLException ex) {
            logger.log(Level.SEVERE, "Unable to populate File Types by MIME Type tree view from DB: ", ex);
        }
    }
    setChanged();
    notifyObservers();
}

