        logger.log(Level.SEVERE, "Error replacing artifact tag. No open case found.", ex);
        Platform.runLater(() -> new Alert(Alert.AlertType.ERROR, Bundle.ReplaceBlackboardArtifactTagAction_replaceTag_alert(oldArtifactTag.getName().getDisplayName(), oldArtifactTag.getArtifact().getArtifactID())).show());
        return null;
    }
    try {
        logger.log(Level.INFO, "Replacing tag {0}  with tag {1} for artifact {2}", new Object[] { oldArtifactTag.getName().getDisplayName(), newTagName.getDisplayName(), oldArtifactTag.getContent().getName() });
        tagsManager.deleteBlackboardArtifactTag(oldArtifactTag);
        tagsManager.addBlackboardArtifactTag(oldArtifactTag.getArtifact(), newTagName, newComment);
    } catch (TskCoreException tskCoreException) {
        logger.log(Level.SEVERE, "Error replacing artifact tag", tskCoreException);
        Platform.runLater(() -> new Alert(Alert.AlertType.ERROR, Bundle.ReplaceBlackboardArtifactTagAction_replaceTag_alert(oldArtifactTag.getName().getDisplayName(), oldArtifactTag.getArtifact().getArtifactID())).show());
    }
    return null;
}

