List<BlackboardArtifact> findArtifactsBySubstring(StringObjectPropertyType item,BlackboardAttribute.ATTRIBUTE_TYPE attrType){
    if (item.getValue() == null) {
        throw new TskCoreException("Error: Value field is null");
    }
    if (item.getCondition() == null) {
        addWarning("Warning: No condition given for " + attrType.getDisplayName() + " field, using substring comparison");
    } else if (item.getCondition() != ConditionTypeEnum.CONTAINS) {
        addWarning("Warning: Ignoring condition " + item.getCondition() + " for " + attrType.getDisplayName() + " field and doing substring comparison");
    }
    List<BlackboardArtifact> hits = null;
    try {
        Case case1 = Case.getCurrentCaseThrows();
        SleuthkitCase sleuthkitCase = case1.getSleuthkitCase();
        String[] parts = item.getValue().toString().split("##comma##");
        if ((item.getApplyCondition() == null) || (item.getApplyCondition() == ConditionApplicationEnum.ANY)) {
            for (String part : parts) {
                if (hits == null) {
                    hits = sleuthkitCase.getBlackboardArtifacts(attrType, part, false);
                } else {
                    hits.addAll(sleuthkitCase.getBlackboardArtifacts(attrType, part, false));
                }
            }
        } else if ((item.getApplyCondition() != null) || (item.getApplyCondition() == ConditionApplicationEnum.ALL)) {
            boolean firstRound = true;
            for (String part : parts) {
                if (firstRound) {
                    hits = sleuthkitCase.getBlackboardArtifacts(attrType, part, false);
                    firstRound = false;
                } else if (hits != null) {
                    hits.retainAll(sleuthkitCase.getBlackboardArtifacts(attrType, part, false));
                } else {
                    return new ArrayList<BlackboardArtifact>();
                }
            }
        } else {
            throw new TskCoreException("Error: Can not apply NONE condition in search");
        }
    } catch (TskCoreException | NoCurrentCaseException ex) {
        addWarning(ex.getLocalizedMessage());
    }
    return hits;
}

List<CombinedEvent> getCombinedEvents(Interval timeRange,RootFilter filter){
    Long startTime = timeRange.getStartMillis() / 1000;
    Long endTime = timeRange.getEndMillis() / 1000;
    if (Objects.equals(startTime, endTime)) {
        endTime++;
    }
    ArrayList<CombinedEvent> results = new ArrayList();
    DBLock.lock();
    final String query = "SELECT full_description, time, file_id, GROUP_CONCAT(events.event_id), GROUP_CONCAT(sub_type)" + " FROM events " + useHashHitTablesHelper(filter) + useTagTablesHelper(filter) + " WHERE time >= " + startTime + " AND time <" + endTime + " AND " + SQLHelper.getSQLWhere(filter) + " GROUP BY time,full_description, file_id ORDER BY time ASC, full_description";
    try (Statement stmt = con.createStatement();
        ResultSet rs = stmt.executeQuery(query)) {
        while (rs.next()) {
            List<Long> eventIDs = SQLHelper.unGroupConcat(rs.getString("GROUP_CONCAT(events.event_id)"), Long::valueOf);
            List<EventType> eventTypes = SQLHelper.unGroupConcat(rs.getString("GROUP_CONCAT(sub_type)"), s -> RootEventType.allTypes.get(Integer.valueOf(s)));
            Map<EventType, Long> eventMap = new HashMap();
            for (int i = 0; i < eventIDs.size(); i++) {
                eventMap.put(eventTypes.get(i), eventIDs.get(i));
            }
            results.add(new CombinedEvent(rs.getLong("time") * 1000, rs.getString("full_description"), rs.getLong("file_id"), eventMap));
        }
    } catch (SQLException sqlEx) {
        LOGGER.log(Level.SEVERE, "failed to execute query for combined events", sqlEx);
    } finally {
        DBLock.unlock();
    }
    return results;
}

