void handleException(Exception exception,String details){
    int status = 0;
    if (exception instanceof TurnBasedMultiplayerClient.MatchOutOfDateApiException) {
        TurnBasedMultiplayerClient.MatchOutOfDateApiException matchOutOfDateApiException = (TurnBasedMultiplayerClient.MatchOutOfDateApiException) exception;
        new AlertDialog.Builder(this).setMessage("Match was out of date, updating with latest match data...").setNeutralButton(android.R.string.ok, null).show();
        TurnBasedMatch match = matchOutOfDateApiException.getMatch();
        updateMatch(match);
        return;
    }
    if (exception instanceof ApiException) {
        ApiException apiException = (ApiException) exception;
        status = apiException.getStatusCode();
    }
    if (!checkStatusCode(status)) {
        return;
    }
    String message = getString(R.string.status_exception_error, details, status, exception);
    new AlertDialog.Builder(this).setMessage(message).setNeutralButton(android.R.string.ok, null).show();
}

