void shutDown(){
    logger.log(Level.INFO, "Keyword search ingest module instance {0} shutting down", instanceNum);
    if ((initialized == false) || (context == null)) {
        return;
    }
    if (context.fileIngestIsCancelled()) {
        logger.log(Level.INFO, "Keyword search ingest module instance {0} stopping search job due to ingest cancellation", instanceNum);
        IngestSearchRunner.getInstance().stopJob(jobId);
        cleanup();
        return;
    }
    IngestSearchRunner.getInstance().endJob(jobId);
    if (refCounter.decrementAndGet(jobId) == 0) {
        try {
            final int numIndexedFiles = KeywordSearch.getServer().queryNumIndexedFiles();
            logger.log(Level.INFO, "Indexed files count: {0}", numIndexedFiles);
            final int numIndexedChunks = KeywordSearch.getServer().queryNumIndexedChunks();
            logger.log(Level.INFO, "Indexed file chunks count: {0}", numIndexedChunks);
        } catch (NoOpenCoreException | KeywordSearchModuleException ex) {
            logger.log(Level.SEVERE, "Error executing Solr queries to check number of indexed files and file chunks", ex);
        }
        postIndexSummary();
        synchronized (ingestStatus) {
            ingestStatus.remove(jobId);
        }
    }
    cleanup();
}

