void removeDupes(List<CharSequence> suggestions,List<CharSequence> stringsPool){
    if (suggestions.size() < 2)
        return;
    int i = 1;
    while (i < suggestions.size()) {
        final CharSequence cur = suggestions.get(i);
        for (int j = 0; j < i; j++) {
            CharSequence previous = suggestions.get(j);
            if (TextUtils.equals(cur, previous)) {
                removeSuggestion(suggestions, i, stringsPool);
                i--;
                break;
            }
        }
        i++;
    }
}

Set<Long> findAllFileIdsWhere(String sqlWhereClause){
    dbWriteLock();
    try {
        if (isClosed()) {
            throw new TskCoreException("The drawables database is closed");
        }
        try (Statement statement = con.createStatement()) {
            ResultSet rs = statement.executeQuery("SELECT obj_id FROM drawable_files WHERE " + sqlWhereClause);
            Set<Long> ret = new HashSet();
            while (rs.next()) {
                ret.add(rs.getLong(1));
            }
            return ret;
        } catch (SQLException ex) {
            throw new TskCoreException(String.format("Failed to query file id for WHERE clause %s", sqlWhereClause), ex);
        }
    } finally {
        dbWriteUnlock();
    }
}

