String getMIMEType(AbstractFile file){
    String mimeType = file.getMIMEType();
    if (null != mimeType) {
        return removeOptionalParameter(mimeType);
    }
    if (!file.isFile() || file.getSize() <= 0 || (file.getType() == TskData.TSK_DB_FILES_TYPE_ENUM.UNALLOC_BLOCKS) || (file.getType() == TskData.TSK_DB_FILES_TYPE_ENUM.UNUSED_BLOCKS) || (file.getType() == TskData.TSK_DB_FILES_TYPE_ENUM.VIRTUAL_DIR) || ((file.getType() == TskData.TSK_DB_FILES_TYPE_ENUM.SLACK) && file.getSize() < SLACK_FILE_THRESHOLD)) {
        mimeType = MimeTypes.OCTET_STREAM;
    }
    if (null == mimeType) {
        mimeType = detectUserDefinedType(file);
    }
    if (null == mimeType) {
        mimeType = detectAutopsyDefinedType(file);
    }
    if (null == mimeType) {
        ReadContentInputStream stream = new ReadContentInputStream(file);
        try (TikaInputStream tikaInputStream = TikaInputStream.get(stream)) {
            String tikaType = tika.detect(tikaInputStream, file.getName());
            mimeType = tikaType.replace("tika-", "");
            mimeType = removeOptionalParameter(mimeType);
        } catch (Exception ignored) {
            mimeType = MimeTypes.OCTET_STREAM;
        }
    }
    file.setMIMEType(mimeType);
    return mimeType;
}

