Map<String, FilesSet> readDefinitionsXML(File xmlFile){
    Map<String, FilesSet> filesSets = new HashMap();
    if (!xmlFile.exists()) {
        return filesSets;
    }
    if (!xmlFile.canRead()) {
        logger.log(Level.SEVERE, "FilesSet definition file at {0} exists, but cannot be read", xmlFile.getPath());
        return filesSets;
    }
    Document doc = XMLUtil.loadDoc(InterestingItemsFilesSetSettings.class, xmlFile.getPath());
    if (doc == null) {
        logger.log(Level.SEVERE, "FilesSet definition file at {0}", xmlFile.getPath());
        return filesSets;
    }
    Element root = doc.getDocumentElement();
    if (root == null) {
        logger.log(Level.SEVERE, "Failed to get root {0} element tag of FilesSet definition file at {1}", new Object[] { FILE_SETS_ROOT_TAG, xmlFile.getPath() });
        return filesSets;
    }
    NodeList setElems = root.getElementsByTagName(FILE_SET_TAG);
    for (int i = 0; i < setElems.getLength(); ++i) {
        readFilesSet((Element) setElems.item(i), filesSets, xmlFile.getPath());
    }
    return filesSets;
}

