void analyzeRegistryFiles(){
    List<AbstractFile> allRegistryFiles = findRegistryFiles();
    FileWriter logFile = null;
    try {
        logFile = new FileWriter(RAImageIngestModule.getRAOutputPath(currentCase, "reg") + File.separator + "regripper-info.txt");
    } catch (IOException ex) {
        logger.log(Level.SEVERE, null, ex);
    }
    for (AbstractFile regFile : allRegistryFiles) {
        String regFileName = regFile.getName();
        long regFileId = regFile.getId();
        String regFileNameLocal = RAImageIngestModule.getRATempPath(currentCase, "reg") + File.separator + regFileName;
        String outputPathBase = RAImageIngestModule.getRAOutputPath(currentCase, "reg") + File.separator + regFileName + "-regripper-" + Long.toString(regFileId);
        File regFileNameLocalFile = new File(regFileNameLocal);
        try {
            ContentUtils.writeToFile(regFile, regFileNameLocalFile, context::dataSourceIngestIsCancelled);
        } catch (ReadContentInputStreamException ex) {
            logger.log(Level.WARNING, String.format("Error reading registry file '%s' (id=%d).", regFile.getName(), regFileId), ex);
            this.addErrorMessage(NbBundle.getMessage(this.getClass(), "ExtractRegistry.analyzeRegFiles.errMsg.errWritingTemp", this.getName(), regFileName));
            continue;
        } catch (IOException ex) {
            logger.log(Level.SEVERE, String.format("Error writing temp registry file '%s' for registry file '%s' (id=%d).", regFileNameLocal, regFile.getName(), regFileId), ex);
            this.addErrorMessage(NbBundle.getMessage(this.getClass(), "ExtractRegistry.analyzeRegFiles.errMsg.errWritingTemp", this.getName(), regFileName));
            continue;
        }
        if (context.dataSourceIngestIsCancelled()) {
            break;
        }
        try {
            if (logFile != null) {
                logFile.write(Long.toString(regFileId) + "\t" + regFile.getUniquePath() + "\n");
            }
        } catch (TskCoreException | IOException ex) {
            logger.log(Level.SEVERE, null, ex);
        }
        logger.log(Level.INFO, "{0}- Now getting registry information from {1}", new Object[] { moduleName, regFileNameLocal });
        RegOutputFiles regOutputFiles = ripRegistryFile(regFileNameLocal, outputPathBase);
        if (context.dataSourceIngestIsCancelled()) {
            break;
        }
        if (regOutputFiles.autopsyPlugins.isEmpty() == false) {
            if (parseAutopsyPluginOutput(regOutputFiles.autopsyPlugins, regFile) == false) {
                this.addErrorMessage(NbBundle.getMessage(this.getClass(), "ExtractRegistry.analyzeRegFiles.failedParsingResults", this.getName(), regFileName));
            }
        }
        if (!regOutputFiles.fullPlugins.isEmpty()) {
            try {
                Report report = // NON-NLS
                currentCase.addReport(// NON-NLS
                regOutputFiles.fullPlugins, // NON-NLS
                NbBundle.getMessage(this.getClass(), "ExtractRegistry.parentModuleName.noSpace"), "RegRipper " + regFile.getUniquePath(), regFile);
                KeywordSearchService searchService = Lookup.getDefault().lookup(KeywordSearchService.class);
                if (null == searchService) {
                    logger.log(Level.WARNING, "Keyword search service not found. Report will not be indexed");
                } else {
                    searchService.index(report);
                }
            } catch (TskCoreException e) {
                this.addErrorMessage("Error adding regripper output as Autopsy report: " + e.getLocalizedMessage());
            }
        }
        regFileNameLocalFile.delete();
    }
    try {
        if (logFile != null) {
            logFile.close();
        }
    } catch (IOException ex) {
        logger.log(Level.SEVERE, null, ex);
    }
}

