Future<Void> transcodeVideo(FileDescriptor inFileDescriptor,String outPath,Listener listener,Future<Void> transcodeVideo,String inPath,String outPath,MediaFormatStrategy outFormatStrategy,Listener listener,Future<Void> transcodeVideo,FileDescriptor inFileDescriptor,String outPath,MediaFormatStrategy outFormatStrategy,Listener listener){
    Looper looper = Looper.myLooper();
    if (looper == null)
        looper = Looper.getMainLooper();
    final Handler handler = new Handler(looper);
    final AtomicReference<Future<Void>> futureReference = new AtomicReference();
    final Future<Void> createdFuture = mExecutor.submit(new Callable<Void>() {
        @Override
        public Void call() throws Exception {
            Exception caughtException = null;
            try {
                MediaTranscoderEngine engine = new MediaTranscoderEngine();
                engine.setProgressCallback(new MediaTranscoderEngine.ProgressCallback() {
                    @Override
                    public void onProgress(final double progress) {
                        handler.post(new Runnable() {
                            @Override
                            public void run() {
                                listener.onTranscodeProgress(progress);
                            }
                        });
                    }
                });
                engine.setDataSource(inFileDescriptor);
                engine.transcodeVideo(outPath, outFormatStrategy);
            } catch (IOException e) {
                Log.w(TAG, "Transcode failed: input file (fd: " + inFileDescriptor.toString() + ") not found" + " or could not open output file ('" + outPath + "') .", e);
                caughtException = e;
            } catch (InterruptedException e) {
                Log.i(TAG, "Cancel transcode video file.", e);
                caughtException = e;
            } catch (RuntimeException e) {
                Log.e(TAG, "Fatal error while transcoding, this might be invalid format or bug in engine or Android.", e);
                caughtException = e;
            }
            final Exception exception = caughtException;
            handler.post(new Runnable() {
                @Override
                public void run() {
                    if (exception == null) {
                        listener.onTranscodeCompleted();
                    } else {
                        Future<Void> future = futureReference.get();
                        if (future != null && future.isCancelled()) {
                            listener.onTranscodeCanceled();
                        } else {
                            listener.onTranscodeFailed(exception);
                        }
                    }
                }
            });
            if (exception != null)
                throw exception;
            return null;
        }
    });
    futureReference.set(createdFuture);
    return createdFuture;
}

