boolean pushTimeRange(Interval timeRange){
    Interval clampedTimeRange;
    if (timeRange == null) {
        clampedTimeRange = this.filteredEvents.getSpanningInterval();
    } else {
        Interval spanningInterval = this.filteredEvents.getSpanningInterval();
        if (spanningInterval.overlaps(timeRange)) {
            clampedTimeRange = spanningInterval.overlap(timeRange);
        } else {
            clampedTimeRange = spanningInterval;
        }
    }
    ZoomParams currentZoom = filteredEvents.zoomParametersProperty().get();
    if (currentZoom == null) {
        advance(InitialZoomState.withTimeRange(clampedTimeRange));
        return true;
    } else if (currentZoom.hasTimeRange(clampedTimeRange) == false) {
        advance(currentZoom.withTimeRange(clampedTimeRange));
        return true;
    } else {
        return false;
    }
}

String format(ExtractedText et,String result){
    int pos = et.selectionStart - 1;
    while (pos > 0 && Character.isWhitespace(et.text.charAt(pos))) {
        pos--;
    }
    if (pos == -1 || mUpperCaseChars.contains(et.text.charAt(pos))) {
        result = Character.toUpperCase(result.charAt(0)) + result.substring(1);
    }
    if (et.selectionStart - 1 > 0 && !Character.isWhitespace(et.text.charAt(et.selectionStart - 1))) {
        result = " " + result;
    }
    if (et.selectionEnd < et.text.length() && !Character.isWhitespace(et.text.charAt(et.selectionEnd))) {
        result = result + " ";
    }
    return result;
}

